css:
@-moz-document domain("community.imgur.com") {
body {

background: #121211;

}

.embedded-posts {
background: #121211 !important;
}

.embedded-posts {
background: #121211 !important;
}

.topic-map .information {
border-top: 2px solid #2b2b2b;
}

.nav-pills>li>a {

border: none;

padding: 5px 12px;

color: #85BF25;

font-size: 1.143em;

line-height: 20px;

transition: background .15s;

}



.alert.alert-info {

background-color: #2b2b2b;

color: #85BF25;

}



.btn {

border: none;

color: #85BF25;

font-weight: normal;

background: #2b2b2b;

}



element.style {

background-color: #2b2b2b;

color: #85BF25;

}



.topic-list.categories .category h3 a[href] {

color: #85BF25;

}



.topic-list.categories>tbody>tr:nth-child(odd) {

background-color: #181817;

}



.topic-list.categories>tbody>tr:nth-child(even) {

background-color: #2b2b2b;

}



.embedded-posts .topic-body {

background: #121211 !important;

box-sizing: border-box;

width: calc(100% - 60px);

overflow: hidden;

padding: 10px 15px 15px 15px;

}



.topic-list>tbody>tr:nth-child(odd) {

background-color: #181817;

}





.topic-list>tbody>tr:nth-child(even) {

background-color: #2b2b2b;

}





.nav-pills>li.active>a, .nav-pills>li>a.active {

color: #fff;

background-color: #10005A;

}



.topic-list th {

color: #FFFF00;

font-weight: normal;

font-size: 1em;

}



.topic-list {

width: 100%;

border-collapse: separate;

border-spacing: 0;

border-top: 1px solid #2b2b2b;

background: #2b2b2b;

}



.d-dropdown {

display: none;

position: absolute;

background: #121211;

max-height: 417px;

top: 100%;

right: 0;

z-index: 1100;

overflow: auto;

border: 1px solid #121211;

padding: 5px;

box-shadow: 0 2px 2px rgba(0,0,0,0.4);

}



.topic-map .map .number, .topic-map .map i {

color: #85BF25;

}



.topic-map {

margin: 20px 0 0 0;

background: transparent;

}



a.mention {

padding: 2px 4px;

color: #85BF25;

background: transparent;

}



#topic-title h1 a {

color: #85BF25;

}



.badge-category[href] {

color: #85BF25;

}



aside.quote .title {

border-left: 5px solid #121211;

background-color: #121211;

color: #5f5f5b;

padding: 12px 12px 1px 12px;

-webkit-user-select: none;

-moz-user-select: none;

-ms-user-select: none;

}



blockquote {

background-color: #121211;

border-left: 5px solid #121211;

overflow: hidden;

}



element.style {

background-color: #B3B5B4;

color: #FFFFFF;

}



#suggested-topics .topic-list>tbody>tr:nth-child(odd) {

background-color: #181817;

}



#suggested-topics .topic-list>tbody>tr:nth-child(even) {

background-color: #2b2b2b;

}



.topic-list a.title {

color: #FFFF00;

}



.d-dropdown#notifications-dropdown .read {

background-color: #2b2b2b;

}



.topic-list td {

color: #85BF25;

font-size: 1em;

}







#topic-footer-buttons p {

line-height: 32px;

color: #85BF25;

}



html {

color: #85BF25;

font-family: Raleway,Monsterrat,T;

font-size: 14px;

line-height: 19px;

background-color: #fff;

}
}
