@-moz-document domain("newsru.com") {
body 
    {
        font: 10pt Verdana,sans-serif;
        font-family: Arial,Helvetica,sans-serif;
        font-size: 12px;
        line-height: 1.5em;
        color: #282828;        
        background: none;
    }
	
table[width="770"]{
  	width:1000px !important;
  	margin:auto;
}
table[width="770"]>tbody>tr>td[width="525"]{
	width:100% !important;
}
	
.spiegelsub,
.headcolumn {
    font-size: 14px;
    line-height: 14px;
    font-weight: bold;
    color: #5367E9;
}	
	#stakan, 
	.down, 
	noindex, 
	iframe, 
	table[width="770"]>tbody>tr>td[width="240"],
