css:
@-moz-document domain("linux.org.ru") {

html, body {

    background-color: #474a51;

}

    .stars {

    color: #fc5e58;

}

    #hd .menu a {

    color: #bbbbbb;

}

    .boxlet {

    background-color: rgba(39, 44, 45, 0) !important;

    border: 1px solid #4a545c;

    border-radius:  0;

}

a {

    color: #bbbbbb;

}

    #bd .btn-default { 

    background-color: rgba(156, 156, 156, 0.14);

}

    #bd .btn {

    border-radius: 0.2em;

    border: none;

    color: #EEEEEC;

    background-image: none; 

    background: rgba(221, 221, 221, 0.14) !important;

}

    .messages .msg {

    background: none repeat scroll 0% 0% rgba(20, 21, 23, 0.03);

}

    #bd .sign a {
        
    color: #c5d0e6;

}

    #bd a.tag {

    color: #bbbbbb;

}

       .entry-body{

       color: #BABDB6 ;   

       filter: brightness(100%);

}

    h2 a{

        color: #c5d0e6 !important;


}

    .code {

    background-color: #4a545c;


}

    input, textarea{

       background: none repeat scroll 0% 0% rgba(186, 189, 183, 0.5);

       border: 1px solid gray !important;      
}

    .infoblock{

        background-color: rgba(20, 21, 23, 0.03) !important;

}

   .group-label {

    background-color: rgba(200, 125, 5, 0.44);

}

    [style^="display: inline-table; width: 75%;"]{

        background-color: transparent !important;

}

    [style^="display: inline-table; width: 75%;"] *{

        color: #BABDB6 !important;

}

     div[style^="display: table-cell;"], div[style^="display: table-cell;"] *{

         border-right: none !important;

         color: #bbbbbb !important;

}

    [style^="display: inline-table; width: 75%;"]:first-child {

        border: 1px solid #555 !important;

}

    a.externallink:after {

        display: none !important;
      
}

    img {

      filter: contrast(100%) brightness(80%);

}
    #bd .forum table thead { 
        background: #5b5e63; 

}
    .message-table .tag {
     background: #5b5e63;

}
    #bd .forum table tbody td {
     border: 1px solid #4a545c;    

}
    #whois_userpic {
    background: none repeat scroll 0% 0% #44474d 
        
}
    #sitetitle {
    color: #bbbbbb;
        
}
    #loginGreating a {
    color: #c5d0e6 !important;
    
}
    #bd h1, #bd h2 {
    color: #c5d0e6;    

}
   .page-number {        
    background-color: #474a51;

} 

   #wiki-page  #user-menu{

        background-color: transparent !important;

}

    #wiki-page #tab_menu *{

        color: #bbbbbb; 

}

    #wiki-page #tab_menu .active *{

        color: #c5d0e6;

}

    #wiki-page ul.tab-menu li {

    background-color: transparent;

}

    #wiki-page #contents{

     background-color: transparent;

}

    #wiki-page  table, #toc{

        background-color: transparent !important;

}
    #user-menu a { 
    color: #c5d0e6;

}
    pre {
    background-color: #4a545c;    
    
}
    #contents { 
    border: 2px solid #4a545c;    
        
}
    #nav-menu ul {
    border-bottom: 1px solid #4a545c;   
        
}
  
}
