css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
.editor-slot {position: relative!important;}
.bubbles {display: list-item!important; position: absolute,important; opacity: 1!important; left: -70px!important; top: -20px!important; width: 32px!important; min-width: 32px!important; list-style: none;}
.bubbles>div {float: none!important; display: list-item!important; margin-bottom: 5px!important; opacity: 1!important;  list-style: none;}
.editor-slot .inline-controls {width: auto!important;}
.editor-slot .inline-controls {display: block!important; position: absolute!important; left: -45px!important; top: -10px!important;}
.tray-open .editor-slot .inline-controls .tray {display: block!important;}
.tray>.control {display: list-item!important; margin-bottom: 13px;}
.tray-open .editor-slot .inline-controls .tray {display: block!important; position: absolute!important; left: -60px!important; top: 36px!important;}

}
