css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("trello.com") {
    
    * {
        transition: all .2s ease-out !important;
    }
    body {
        background: url("http://subtlepatterns.com/patterns/binding_dark.png") repeat transparent !important;

    }
    #header {
        background: none repeat scroll 0 0 rgba(0, 0, 0, 0.15);
    }
    textarea, .action-comment, .list-card, .card-detail-desc p {
   
        font-size: 16px !important;
        line-height: 1.2 !important;
    }
    .list {
        background: rgba(100, 100, 100, 0.2) !important;
        color: #FFF;
        border: 1.5px dashed rgba(255, 255, 255, 0.2);
    }
    .list-header, .card-label {
        text-align: center;
    }
    .list-header-name {
        text-align: center;
        font-family: "B Yekan";
        font-weight: normal;
        font-size: 17px;
    }
    .list-card-details {
        direction: rtl;
        font-family: "B Yekan";
    }
    .list-card-details .list-card-edit-title {font-family: "tahoma";font-size: 12px !important;;}
    .list-card-labels .card-label {
        float: right;
    }
    .badges {
        float: right;
    }
    .badge-text {
        float: none !important;
    }
    .list-card-operation {
        width: 16px !important;
    }

    .button-link:hover {
        background: #444 !important;
        border-color: #111 !important;
    }
    .js-archive-card:hover {
        background: tomato !important;
        border-color: red !important;
    }
    .open-card-composer, .js-open-add-list {
        color: #FFFFFF;
    }
    .open-card-composer {
        font-size: 0;
        text-align: right;
    }
    .open-card-composer:before {
        font-size: 13px;
        content: "افزودن کارت جدید";
        font-family: "B Yekan";
    }
    .open-card-composer:hover, .js-open-add-list:hover {
        color: #222 !important;
        background: #FFF;
        text-decoration: none;
    }
    /********** list **********/
    .pop-over .header-title {
        font-size: 0;
    }
    .pop-over .header-title:before {
        content: "لیست دستورات";
        font-size: 13px;
        font-family: "B Yekan";
    }
    .edit-heavy .field {
        text-align: center;
        font-family: "B Yekan";
        color: #FFFFFF;
        font-weight: normal;
        border: 1px dashed #4B4B4B !important;
        height: 40px !important;
    }
    .pop-over .pop-over-list {direction:rtl;}
    .pop-over .pop-over-list li > a {font-weight: normal;}
    .pop-over .pop-over-list li > a.js-add-card {font-size: 0;text-align: right;}
    .pop-over .pop-over-list li > a.js-add-card:before {font-size: 13px;font-family: "B Yekan";content: "افزودن کارت...";}
    .pop-over .pop-over-list li > a.js-copy-list {font-size: 0;text-align: right;}
    .pop-over .pop-over-list li > a.js-copy-list:before {font-size: 13px;font-family: "B Yekan";content: "کپی لیست...";}
    .pop-over .pop-over-list li > a.js-move-list {font-size: 0;text-align: right;}
    .pop-over .pop-over-list li > a.js-move-list:before {font-size: 13px;font-family: "B Yekan";content: "انتقال لیست...";}
    .pop-over .pop-over-list li > a.js-list-subscribe {font-size: 0;text-align: right;}
    .pop-over .pop-over-list li > a.js-list-subscribe:before {font-size: 13px;font-family: "B Yekan";content: "عضویت...";}
    .pop-over .pop-over-list li > a.js-move-cards {font-size: 0;text-align: right;}
    .pop-over .pop-over-list li > a.js-move-cards:before {font-size: 13px;font-family: "B Yekan";content: "انتقال تمامی کارت های داخل این لیست...";}
    .pop-over .pop-over-list li > a.js-archive-cards {font-size: 0;text-align: right;}
    .pop-over .pop-over-list li > a.js-archive-cards:before {font-size: 13px;font-family: "B Yekan";content: "آرشیو کردن تمامی کارت های داخل این لیست...";}
    .pop-over .pop-over-list li > a.js-close-list {font-size: 0;text-align: right;}
    .pop-over .pop-over-list li > a.js-close-list:before {font-size: 13px;font-family: "B Yekan";content: "آرشیو کردن این لیست...";}
    #board {
        direction: rtl;
    }
    textarea.list-card-composer-textarea, textarea.list-card-composer-textarea:focus {
        color: #ffffff;
    }
    .list.add-list .placeholder {
        font-size: 0;
        text-align: right;
    }
    .list.add-list .placeholder:before {
        font-size: 13px;
        content: "افزودن یک لیست";
        font-family: "B Yekan";
    }

    .list-card {
        background-color: #333339 !important;
        color: #EEE !important;
        border-bottom: 1px solid rgba(0,0,0,0.5) !important;
        border-right: 1px solid rgba(0,0,0,0.2) !important;
        border-width: 2px !important;
        border-radius: 0px 3px;
    }

    .list-card a {
        color: #EEE !important;
    }
    .list-card-operation {
        left: 0;
        right: inherit;
        border-radius: 3px 0 3px 0;
    }
    .list-header-menu-icon {
        left: -5px;
        right: inherit;
    }
    .js-edit-labels .quick-card-editor-buttons-item-text {font-size: 0;}
    .js-edit-labels .quick-card-editor-buttons-item-text:before {font-size: 13px;font-family: "B Yekan";content: "ویرایش لیبل";}
    .js-edit-members .quick-card-editor-buttons-item-text {font-size: 0;}
    .js-edit-members .quick-card-editor-buttons-item-text:before {font-size: 13px;font-family: "B Yekan";content: "تغییر کاربر";}
    .js-move-card .quick-card-editor-buttons-item-text {font-size: 0;}
    .js-move-card .quick-card-editor-buttons-item-text:before {font-size: 13px;font-family: "B Yekan";content: "انتفال";}
    .js-copy-card .quick-card-editor-buttons-item-text {font-size: 0;}
    .js-copy-card .quick-card-editor-buttons-item-text:before {font-size: 13px;font-family: "B Yekan";content: "کپی";}
    .js-edit-due-date .quick-card-editor-buttons-item-text {font-size: 0;}
    .js-edit-due-date .quick-card-editor-buttons-item-text:before {font-size: 13px;font-family: "B Yekan";content: "تغییر تاریخ انجام";}
    .js-archive .quick-card-editor-buttons-item-text {font-size: 0;}
    .js-archive .quick-card-editor-buttons-item-text:before {font-size: 13px;font-family: "B Yekan";content: "آرشیو";}
    /********** board **********/
    .sidebar-boards-list, .board-list {
        opacity: 0.50;
        margin-left: inherit;
        margin-right: 38px;
    }
    .board-list a {
        margin: 0 0 16px 16px;
    }
    .window-module-title {
        direction: rtl;
        margin: 0 38px 4px 0;
    }
    .window-module-title-icon {
        left: inherit;
        right: -38px;
    }
    .window-module-title h3 {
        color: #a6a6a6;
    }
    .sidebar-boards-list .details,
    .board-list .details,
    .sidebar-boards-list li.starred .details,
    .board-list li.starred .details {
        padding-right: inherit;
        padding-left: 36px;
        text-align: right;
    }
    .sidebar-boards-list .unread-indicator, .board-list .unread-indicator {
        right: inherit;
        left: 12px;
    }
    .sidebar-boards-list .icon-star, .board-list .icon-star {
        right: inherit;
        left: 0;
    }
    .dialog-close-button {
        left: 0;
        right: inherit;
    }
    .window-header {
        text-align: right;
        margin: 4px 38px 4px 40px;
    }
    .window-header-icon {
        right: -28px;
        left: inherit;
    }
    .window-title {
        direction: rtl;
    }
    .window-title .window-header-inline-content p.inline-block {font-size: 0;}
    .window-title .window-header-inline-content p.inline-block:before {font-size: 13px;font-family: "B Yekan"; content: "در لیست"}
    .window-title .window-header-inline-content p.inline-block a.js-open-move-from-header {font-size: 13px;margin: 0 10px 0 0;font-family: "B Yekan";font-weight: normal;}
    .window-title .window-header-inline-content p.inline-block a.js-open-move-from-header strong {font-weight: normal;}
    .window-main-col {}
    .window-sidebar {}
}
