css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.mafiascum.net") {
html, body {
	color: #536482;
	background-color: #F1BBBA;
}

body {
	/* Text-Sizing with ems: http://www.clagnut.com/blog/348/ */
	font-family: segoe script, Verdana, Helvetica, Arial, sans-serif;
	color: #774F38;
	background-color: #F1BBBA;
	/*font-size: 62.5%;			 This sets the default font size to be equivalent to 10px */
	font-size: 10px;
	margin: 0;
	padding: 12px 0;
}
a#logo {
  display: none;
}
span.corners-top {
	background-image: url("http://i.imgur.com/YrG0Asm.png");
}

span.corners-top span {
	background-image: url("http://i.imgur.com/TxLGZ6g.png");
}

span.corners-bottom {
	background-image: url("http://i.imgur.com/YrG0Asm.png");
}

span.corners-bottom span {
	background-image: url("http://i.imgur.com/TxLGZ6g.png");
}
h1 {
	/* Forum name */
	font-family: segoe script, Verdana, Helvetica, Arial, sans-serif;
	margin-right: 200px;
	color: #EB9F9F;
	margin-top: 10px;
	/*font-weight: bold;*/
	font-size: 2.5em;
}
h2 {
	/* Forum header titles */
	font-family: segoe script, Verdana, Helvetica, Arial, sans-serif;
	font-weight: normal;
	color: #3f3f3f;
	font-size: 2.1em;
	margin: 0.8em 0 0.2em 0;
}
h3 {
	font-family: segoe script, Verdana, Helvetica, Arial, sans-serif;
	border-bottom-color: #CCCCCC;
	color: #6B5344;
}
fieldset {
	border-width: 0;
	font-family: segoe script, Verdana, Helvetica, Arial, sans-serif;
	font-size: 1.1em;
}
a.forumtitle {
	font-family: segoe script, Verdana, Helvetica, Arial, sans-serif;
	font-size: 1.2em;
	font-weight: bold;
	color: #898989;
	text-decoration: none;
}
a.topictitle {
	font-family: segoe script, Verdana, Helvetica, Arial, sans-serif;
	font-size: 1.2em;
	font-weight: bold;
	color: #898989;
	text-decoration: none;
}
li.header dt, li.header dd {
	line-height: 1em;
	border-left-width: 0;
	margin: 2px 0 4px 0;
	color: #FFFFFF;
	padding-top: 2px;
	padding-bottom: 2px;
	font-size: 1em;
	font-family: segoe script, Verdana, Helvetica, Arial, sans-serif;
	text-transform: uppercase;
}
.content {
	min-height: 3em;
	overflow: hidden;
	line-height: 1.4em;
	font-family: segoe script, Verdana, Helvetica, Arial, sans-serif;
	font-size: 1em;
	color: #6B5344;
	padding-bottom: 1px;
}
p.author {
	margin: 0 15em 0.6em 0;
	padding: 0 0 5px 0;
	font-family: segoe script, Verdana, Helvetica, Arial, sans-serif;
	font-size: 1em;
	line-height: 1.2em;
}

.postprofilecontainer {
	width: 170px;
	float: left;
	margin-right: 3px;
	margin-bottom: 3px;
	min-height: 100px;
	border-right: 2px solid #EDE5CE;
}
	.back2top {
	clear: both;
	height: 30px;
	text-align: right;
	border-top: 1px solid #EDE5CE;
}

.headerbar {
	background: #6B5344 none repeat-x 0 0;
	color: #EB9F9F;
	margin-bottom: 4px;
	padding: 0 5px;
}
.navbar {
	background-color: #6B5344;
	padding: 0 10px;
}
.forabg {
	background: #6B5344 none repeat-x 0 0;
	margin-bottom: 4px;
	padding: 0 5px;
	clear: both;
}
a:link	{ color: #A79C8E; }
a:visited	{ color: #A79C8E; }
a:hover	{ color: #DF1B1B; }
a:active	{ color: #1D316F; }
ul.forums {
	background-color: #F8ECC9;
	background-image: url("http://forum.mafiascum.net/styles/prosilver/theme/images/gradient.gif");
}
ul.topiclist li {
	color: #6B5344;
}
.forumbg {
	background-color: #6B5344;
	background-image: url("http://forum.mafiascum.net/styles/prosilver/theme/images/bg_header.gif");
}
.bg1	{ background-color: #F8ECC9; }
.bg2	{ background-color: #EEE2BF; }
.bg3	{ background-color: #F8ECC9; }
.postbody {
	color: #6B5344;
}
.postprofile strong {
	color: #6B5344;
}
.postprofile a:link, .postprofile a:visited, .postprofile dt.author a {
	color: #A79C8E;
}
li.row:hover {
	background-color: #D9CBA3;
}
.content h2, .panel h2 {
	color: #6B5344;
	border-bottom-color:  #CCCCCC;
}
.panel {
	background-color: #EDE5CE;
	color: #28313F;
}
blockquote {
	background-color: #EDE5CE;
	background-image: url("http://forum.mafiascum.net/styles/prosilver/theme/images/quote.gif");
	border-color:#565667;
}
blockquote blockquote {
	/* Nested quotes */
	background-color:#E4D8B5;
}
blockquote blockquote blockquote {
	/* Nested quotes */
	background-color: #EDE5CE;
}
blockquote blockquote blockquote blockquote {
	/* Nested quotes */
	background-color:#E4D8B5;
}

blockquote blockquote blockquote blockquote blockquote {
	/* Nested quotes */
	background-color: #EDE5CE;
}

blockquote blockquote blockquote blockquote blockquote blockquote {
	/* Nested quotes */
	background-color:#E4D8B5;
}
li.row {
	background-image: url("http://fc08.deviantart.net/fs70/f/2012/249/b/e/free_to_use_cake_icon_by_artisticanubis-d5dt0ha.gif"); background-repeat: no-repeat; background-size: 4em; background-position: 4px ;
}
.post-icon span		{ background-image: url("http://i.imgur.com/sH8Z1s3.png"); }
.reply-icon span	{ background-image: url("http://i.imgur.com/a3DoifJ.png"); }
.locked-icon span	{ background-image: url("http://i.imgur.com/Du9xHcX.png"); }
li.row {
	border-top-color:  #EDE5CE;
	border-bottom-color: #EDE5CE;
}
	ul.topiclist dd {
	border-left-color: #EDE5CE;
}

.rtl ul.topiclist dd {
	border-right-color: #EDE5CE;
	border-left-color: transparent;
}
}
