@namespace url(http://www.w3.org/1999/xhtml);

/* Login / logout box - mbank online & mtransfer */

@-moz-document regexp("https://online\\.mbank\\.pl/[a-z]+/Login"), regexp("http://www\\.mbank\\.pl/logoutpage\\.html\\?*.*"), regexp("https://online\\.mbank\\.pl/[a-z]+/mTransfer\\?*.*") {
/* Remove ads carousel (below the login box) */
  #advertise-slider {
    display: none !important;
  }
}

@-moz-document regexp("https://online\\.mbank\\.pl/.*") {
/* *** Page header *** */

#header {
  height: 125px !important;
}

#header h1, #header #user-profile {
  top: 10px !important;
}

#header #user-profile {
  left: 64px !important;
  width: 873px !important;
}

#header #user-profile .user-name {
  right: auto !important;
  left: 73px !important;
  text-align: left !important;
}

[class^="receivers-list"] [class^="receiver-details"] {
  height: 35px !important;
  line-height: 30px !important;
}


[class^="receivers-list"] [class^="receiver-details"] [class^="receiver-photo"] {
  display: none;
}

#address-books .contact-accounts .transfers>li {
  line-height: 15px;
  padding: 0 10px !important;
  margin: 0 0 5px !important;
}

.contacts-list .records-view li {
  padding: 5px 27px 5px 22px !important;
  }


#address-books .contact-details .record-details .actions+.lists {
  margin: 5px 0 0 !important;
  height: 25px !important;
}

#address-books .contact-details .record-details .bordered li {
  margin: 0 !important;
}

#address-books .contact-details .record-details .lists li {
  line-height: 25px !important;
  
}

.contacts-list .records-view ul.records li h3 {
 line-height: 10px;
}

.contacts-list .records-view ul.records li h3 strong {
 color: #bb9696;
 font-size: 10px;
 font-weight: normal;
}

.contacts-list .records-view ul.records li div.titles {
 color: #000;
 font-weight: bold;
}


.history-region section.operations .operation-row span.buttons {
 display: block !important;
  left: 0 !important;
    bottom: 0 !important;
}

 .history-region section.operations .operation-row span.buttons a {
      -webkit-border-radius: 0;
  border-radius: 0;
  background: none;
  color: #f00;
  box-shadow: none;
  -webkit-box-shadow: none;
  border: 0;

    }
    
    .history-region section.operations .operation-row span.buttons a:hover {
      text-decoration: underline !important;
        }
  
.history-region section.operations .operations-recent-list .operation-row
{
 height: 46px !important;    
}
