css:
@-moz-document url-prefix("https://hall.com/rooms"), url-prefix("http://hall.com/rooms") {
.hall-listview-chat .hall-listview-li {
  margin: 0 -16px 0px;
  padding: 5px 16px 5px 54px;
}

.hall-listview-chat .hall-listview-li + .hall-listview-li.nested {
  padding-top: 0;
  margin-top: -5px;
}

.hall-listview-chat .hall-listview-li.nested + .hall-listview-li:not(.nested) {
  margin-top: -15px;
}

.hall-listview-chat .hall-listview-li + .hall-listview-li:not(.nested) {
  border-top: 1px solid #eee
}

.hall-listview-chat .hall-listview-li:last-child {
  border-bottom: 1px solid #eee;
}

.hall-listview-chat .hall-listview-li:nth-child(even) {
  /*background-color: rgba(0, 0, 0, 0.005);*/
}

.hall-listview-chat .hall-listview-li .hall-profile-image .frame {
  width: 26px !important;
  height: 26px !important;
  background-size: 26px 26px !important;
}

.hall-listview-chat .hall-listview-li .hall-profile-image .image,
.hall-listview-chat .hall-listview-li .hall-profile-image .initials {
  width: 100% !important;
  height: 100% !important;
}

.hall-listview-chat .hall-listview-li .hall-profile-image .image {
  background-size: 26px 26px !important;
}

.hall-listview-chat .hall-listview-li .hall-profile-image {
  top: 0 !important;
  left: 16px !important;
}

.hall-listview-chat .hall-listview-li .hover-area .msg.content,
.hall-listview-chat .hall-listview-li .hover-area .attach.content,
.hall-listview-chat .hall-listview-li .hover-area .link-preview {
  margin-top: -20px;
  margin-bottom: 18px;
  margin-left: 220px;
}

.hall-listview-chat .hall-listview-li:after {
  content: "";
  position: absolute;
  background: #FDFDFD;
  top: 0;
  left: 0;
  bottom: 0;
  width: 264px;
  z-index: -1;
  border-right: 1px solid #eee;
}

.hall-listview-chat .hall-listview-li[data-message-type="Service"] {
  background-color: rgba(0, 0, 0, 0.02);
}

.hall-listview-chat .hall-listview-li[data-message-type="Service"] .hall-profile-image {
  margin-top: 0 !important;
}

.hall-listview-chat .hall-listview-li[data-type="Service"] .message-mod {
  border-left: 0;
  padding-left: 0;
}
.hall-listview-chat .hall-listview-li[data-message-type="Service"] + .hall-listview-li {
  margin-top: -1px !important;
  padding-top: 10px !important;
}
.hall-listview-chat .hall-listview-li .msg.content {
  width: auto;
  padding-right: 0;
}
}
