css:
@-moz-document url-prefix("https://www.tumblr.com/") {
#header .tab .tab_notice {
background: #000000;
}


body {
background-attachment: fixed !important;
}

ol#posts li.notification {
	border-top: 1px solid  !important;
	border-left: 1px solid  !important;
	border-bottom: 1px solid  !important;
	border-right: 1px solid  !important;
	text-decoration: none !important;
}

#posts .notification blockquote {
border-left: solid 3px #a7a7c7 !important;
}

#posts .notification .avatar_frame {
background-color: #000000;
border: 1px solid #000000;
}

.notification .notification_sentence a.username {
        padding-left: 30px
      }

#posts .post.post_full {
padding-right: 0px;
padding-left: 0px;
}

#header .tab_notice.new_post_notice_container {
background-color: rgba(220,50,50,250)
}
#header .tab_notice.inbox_notice_container {
background-color: rgba(220,50,50,250)
}

#posts .notification_sentence {
padding-right: 0px;
padding-left: 0px;
}

.post.is_mine.with_avatar.new_post {
  	color: #444444;
	font-family:courier, monospace;
	background: rgba(0,0,0,0.50) !important; 
}
#new_post{
background: #ffffff !important;
background-repeat: no-repeat !important; 
}

#posts .post.new_post .new_post_label {
-webkit-font-smoothing: antialiased;
opacity: .9;
font-size: 14px;
font-weight: normal;
text-decoration: none;
text-transform: uppercase;
text-align: center;
width: auto;
height: 0px;
position: relative;
}

	.l-content {
	 background: rgba(81, 102, 134, .5) !important; 
}

	.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
background: none !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-bottom: solid 11px rgba(00,00,00, 0.50) !important;
border-right: 11px solid transparent !important;
border-left: 11px solid transparent !important;
}


#content {
	padding-top: 25px !important; 
	padding-bottom: 25px !important; 
	-moz-border-radius: 20px !important; 
}

.radar {
  display: none !important; 
}

.selected .following, .selected .followers, .selected .settings, .selected .posts, .selected .messages, .selected .drafts, .selected .queue, .selected .likes  {
	 color: rgba(0, 0, 0, 0.50) !important; 
	background: rgba(0, 0, 0, 0.0) !important; 
}

#new_post .arrow {
opacity: 0.50 !important;
 margin-left: -1px !important; 
}



#posts .post {

-webkit-border-radius: 10px;
-moz-border-radius: 10px;
border-radius: 10px;
background-color: #ffffff;

position: relative;
border-bottom: solid 2px #BBB;
word-wrap: break-word;
}

#posts .post .footer_links .tags a {
margin-right: 7px;
}

.l-header .tab_bar .tab .tab_notice{
    background-color: rgba(249, 11, 11, 1) !important;
        box-shadow: 0px 0px 15px #888888;
         }

.plexi.show.color {
    background: none
}
.l-container.l-container--two-column-dashboard .left_column {
  background: none
}
.l-container.l-container--two-column-dashboard .right_column {
  background: none
}
.site-notification {
  display: none
}
.l-header-container--refresh {  
  background-color: rgba(54, 70, 93, 0.76);
}
.identity-refresh .l-container.l-container--two-column-dashboard .left_column {
    background-color: rgba(54, 70, 93, 0);
}  
.identity-refresh .l-container.l-container--two-column .right_column, .identity-refresh .l-container.l-container--two-column-dashboard .right_column {

    width: 229px;
    padding-top: 10px;
}
.identity-refresh .tab_notice .tab_notice_value {
    color: rgba(255, 255, 255, 1);
}
.elevator {
    font-size: 75px;
}
.identity-refresh .plexi.show.color {
    background: none repeat scroll 0% 0%;
}  
.identity-refresh .glass.blue, .identity-refresh #hello_glass.blue, .identity-refresh #glass_overlay.blue {
  background: none repeat scroll 0% 0%;
}
.notification .avatar_frame img, .notes .note .avatar_frame img, .ui_avatar .frame img {
border-radius: 2px;
  display: block;
}


.post-typography-update .post .post-content a, .post-typography-update .post .post_content a {
	padding-bottom: .15em
}
.post-typography-update .post.is_conversation .post_content .post_title, .post-typography-update .post.is_regular .post_content .post_title, .post-typography-update .post .post-content .title-field {
  		font-size: 20px;
        line-height: 27px;
        text-decoration: bold;
	 	border-color: #000000 !important; 
  		border-top-width: 2px !important;
   	 	border-left-width: 2px !important;
  	 	border-right-width: 2px !important;
 		border-bottom-width: 2px !important;
        
}

b {
  font-size: 15px !important
}




.post-typography-update .post .post-content .title-field {
    border-style: dashed !important;
    border-width: 1px !important;
    border-color: rgb(175,175,175) !important;
                  }
.post-typography-update .post .post-content .caption-field {
    border-style: dashed !important;
    border-width: 1px !important;
  	border-color: rgb(175,175,175)
                  }
.post-form--tag-editor {
    border-style: dashed !important;
    border-width: 1px !important;
	border-color: rgb(175,175,175)
        }
}
