css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nationstates.net") {
  
  a {
   outline: 0;
  }
  
  .button, textarea, input[type="text"], #loginbox input, input[type="password"], table.settings input[type="text"], table.settings input[type="password"], input[name="report_hours"],     input[name="region_name"], select, .minorinfo, div.tg, textarea, div.divindent,  #page_activity .panelselector, td, th, fieldset, legend, table, select.slim, li.chosendiloption,  #panel ul.popoutmenu, .popupbox, #loginbox, table.settings, div.product, #faq_content, #faq_content h3, p.author, div.newsstory, .frdata, .notificationnumber {
    border-radius: 0px;
  }
    
  a:hover {
    text-decoration: none;
  }
   span:hover {
    text-decoration: none;
  }
  .newtitlename a:hover {
  text-shadow: 2px 2px 5px;
  }
  .bel bannernation{
    width:194px;
  }
  .bannernation > div:nth-child(1){
    width:194px;
  }
  #banner .bel{
    max-width: 194px;
  }
  .bel .bannernation2{
    width: 194px;
  }
  #banner .bannernation a.bellink.mediumname{
    font-size:20px;
  }
  
  #minormenuitems {
    margin-top: 0px;
  }
  #lthreads{
    position: absolute;
    top:390px;
    width: inherit;
    margin: 0px;
    text-align: center;
  }
  
  #loggedin #panel, #loggedin #content {
    margin-top: 62px;
  }
  
  #panel ul.threads {
    position: absolute;
    top:420px;
    width: inherit;
    box-shadow: 0 3px 2px -2px #666;
  }
  
  .panelcontent {
    background: inherit;
    width: 194px;
    position: fixed;
    border-radius: 0px;
    padding-bottom: 0px;
    margin-left: 0px;
    padding-left: 0px;
  }
  
  #panel ul.menu a:hover{
    text-shadow: 0px 0px .4px;
  }
  
  #panel ul.menu li{
  padding: 0px;  
  }
  
  #accessiblitylink {
    position: fixed;
    bottom: 0px;
    left: 0px;
  }
  
  .popupbox {
    padding: 10px;
  }
  
  table.shiny td {
    border: 0px;
    border-radius: 0px;
  }
  
  div.dispatchlist {
    border: 0px;
    border-radius: 0px;
  }
  
  .newmainlinebubbletop {
    border-top-right-radius: 0;
    border-top-left-radius: 0;
  }
  
  .newmainlinebubblebottom {
    text-shadow: none;
    border-bottom-left-radius: 2px;
    border-bottom-right-radius: 2px;
  }
  
  .newmainlinebubblebottom:hover {
    text-shadow: 0px 0px 5px;
  }
  
  .nationsummary p {
    font-family: arial,Tahoma,sans;
    line-height: 1em;
    font-size: 17px;
    margin-left: 12px;
    margin-right: 12px;
  }
  
  .newtitlename {
    font-family: arial,sans;
    text-shadow: 2px 2px 3px #999;
  }
  
  #paneladbox, #paneltitle, #createdby9, #footbar {
    display: none;
  }
}
