css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.dailymail.co.uk"), domain("dailymail.co.uk") {
    
    
  @font-face {
  font-family: 'Cinzel';
  font-style: normal;
  font-weight: 900;
  src: local('Cinzel-Black'), url(http://fonts.gstatic.com/s/cinzel/v4/u8CPqNcpTcF_eDy_SWp9tZBw1xU1rKptJj_0jans920.woff2) format('woff2'), url(http://fonts.gstatic.com/s/cinzel/v4/u8CPqNcpTcF_eDy_SWp9tbO3LdcAZYWl9Si6vvxL-qU.woff) format('woff');
}


  .related-carousel.with-fb, .art-ins, #articleIconLinksContainer, .beta,  .article-text #most-read-news-wrapper, #most-watched-videos-wrapper, .carousel-holder  {
    display: none !important;
     }
  
.column-split, .alpha {
    width: 100% !important;
    }
    
  .alpha .article-text {
      width: 725px !important;
     }
    .article-large .articletext-holder {
        width: 608px !important;
       }
    
    .column-split .article-large .articletext-holder {
        padding: 0 0 0 10px !important;
       }
    .article .articletext img {
        padding: 0 10px 0 0 !important;
       }
    
  .molads_ldr_top_on .page-banner {
    margin-top: -130px !important;
     }
  
 .article-text .mol-para-with-font, .article p {
   font-size: 1.9em !important;
    } 
  .article-small h2, .article-smallslant h2, .article-star-search h2, .article-star-search h3, #infinite-list h3 {
      font-size: 1.8em !important;
      font-family: 'Cinzel', serif !important;
      font-weight: 900 !important;
     }
    h2.linkro-darkred a, h1 {
        font-family: 'Cinzel', serif !important;
        font-weight: 900 !important;
    }
  a {
       transition-property: all !important;
       transition-duration:  .25s !important;
       transition-timing-function: ease-in !important;
       }
  
}
