css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://localhost:8090/") {
    body {
    background-color: #1A1B1F !important;
    color: #CCC !important;
}
    body, table, textarea {
    font-size: 13px !important;
}
    h4 {
    font-size: 1.3em !important;
}
    a:link {
    text-decoration: none !important;
}
    .navbar-default {
    background-color: #222 !important;
    border-bottom: 2px solid #375EAA !important;
}
    fieldset.maininput, fieldset.yacys {
    background-color: #101010 !important;
    border-radius: 5px;
}
    fieldset {
    color: #4B80E7 !important;
}
    .searchresults h4 a {
    font-size: 1.2em;
    font-weight: normal;
    color: #CCC !important;
}
    .snippetLoaded b {
    color: #4B80E7 !important;
}
    a:link {
    color: #4B80E7 !important;
    background-color: transparent;
}
    .searchresults .url a {
    color: #39B364 !important;
}
    .dropdown-menu {
    background-color: #101010 !important;
    box-shadow: 0px 6px 12px rgba(0, 0, 0, 0.176);
}
    .dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus {
    color: #262626;
    text-decoration: none;
    background-color: #242424 !important;
}
    .dropdown-menu .divider {
    background-color: #777 !important;
}
    .btn-default {
    color: #BBB !important;
    background-color: #101010 !important;
    border-color: #777 !important;
}
    .btn-default:hover, .btn-default:focus, .btn-default:active, .btn-default.active, .open > .dropdown-toggle.btn-default {
    color: #333;
    background-color: #242424 !important;
    border-color: #777 !important;
}
    .form-control {
    color: #BBB !important;
    background-color: #242424 !important;
    border: 1px solid #777 !important;
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.075) inset;
}
    .tt-dropdown-menu {
    width: 560px;
    background-color: #242424 !important;
    border: 1px solid #000 !important;
}
    .progress {
    background-color: #345 !important;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.1) inset;
}
    .bootstrap-switch .bootstrap-switch-label {
    color: #BBB !important;
    background: none repeat scroll 0% 0% #101010 !important;
}
    .bootstrap-switch {
    border: 1px solid #777 !important;
}
    .nav > li > a:hover, .nav > li > a:focus {
    text-decoration: none;
    background-color: #101010 !important;
}
    .menugroup h3 {
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    background-color: #345 !important;
    color: #BBB !important;
}
    .pagination > li > a, .pagination > li > span {
    text-decoration: none;
    background-color: #101010;
    border: 1px solid #777 !important;
}
    .pagination > .disabled > span, .pagination > .disabled > span:hover, .pagination > .disabled > span:focus, .pagination > .disabled > a, .pagination > .disabled > a:hover, .pagination > .disabled > a:focus {
    background-color: #242424;
}
 .pagination > .active > a, .pagination > .active > span, .pagination > .active > a:hover, .pagination > .active > span:hover, .pagination > .active > a:focus, .pagination > .active > span:focus {
    z-index: 2;
    color: #000 !important;
}
    .bootstrap-switch .bootstrap-switch-handle-on.bootstrap-switch-warning, .bootstrap-switch .bootstrap-switch-handle-off.bootstrap-switch-warning {
    background: none repeat scroll 0% 0% #242424 !important;
    color: #BBB !important;
}
    
}
