css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("btc-e.com") {


#content > div:nth-child(1) > div:nth-child(1) {display:none !important;}

div.inblock:nth-child(14) {display:none !important;}

#footer {display:none !important;}

div.block:nth-child(3) {display:none !important;}

#content > div:nth-child(2) > div:nth-child(2) {display:none !important;}

#header-logo {display:none !important;}

#header-ticker {display:none !important;}

#nav-container {display:none !important;}

#header {height: 1px;}

/*p.gray:nth-child(13) {display:none !important;}*/
    
p.gray:nth-child(13) {border: 0px; padding: 0px; margin: 0px 10px 0px;}
    
.pairs {display:none !important;}

#content > div:nth-child(1) > div:nth-child(2) {display: table;}

div.inblock:nth-child(12) {display: table-header-group;}
    
#chart_div {display: table-footer-group;}
    
div.inblock:nth-child(12) > h3:nth-child(1) {padding: 0px; margin: 0px 5px 0px; border-radius: 3px;}
    
div.inblock:nth-child(12) {border: 1px solid #000000; border-radius: 3px;}

.profile {background: #EFEFEF;}
    
#header-profile > div:nth-child(2) {width: 350px;
height: 82px;
overflow: hidden;
    overflow-x: hidden;
    overflow-y: hidden;
float: left;}
    
#login_con {width: 330px; background: #EFEFEF;
margin: 11px auto auto;
    margin-top: -10px;
padding: 3px;
    padding-top: 3px;
box-shadow: 0px 0px 6px #FFF;
border-radius: 4px;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    border-bottom-right-radius: 4px;
    border-bottom-left-radius: 4px;
border: 1px solid #B7B7B7;
    border-top-width: 1px;
    border-bottom-width: 1px;
    border-top-style: solid;
    border-bottom-style: solid;
    border-top-color: #B7B7B7;
    border-bottom-color: #B7B7B7;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image-source: none;
    border-image-slice: 100% 100% 100% 100%;
    border-image-width: 1 1 1 1;
    border-image-outset: 0 0 0 0;
    border-image-repeat: stretch stretch;}

#header-profile {position: fixed; right: 10px; bottom: 10px; z-index: 99; }
    
#content {width: 1230px; }
    
#content > div:nth-child(2) > div:nth-child(1) {width: 535px;}

#nChat {width: 535px;}
    
div.inblock:nth-child(10) > div:nth-child(5) {height: 86px;}
    
div.inblock:nth-child(9) > div:nth-child(5) {height: 86px;}
    
#orders-b-list > p:nth-child(1) {display:none !important;}
    
#orders-s-list > p:nth-child(1)  {display:none !important;}  
    
#content > div:nth-child(2) > div:nth-child(1) {height: 679px;}
    
#nChat {height: 660px;}
    
#nChatInput {width: 510px;}

    }
