css:
@-moz-document domain("gmot.nl") {
.mainstrip, .mainstrip_first, .mainstrip_last {
   background: #596876 !important; 
   border-bottom: 4px solid #47525C;
}
img[src*="http://www.gmot.nl/Themes/dilbermc/images/lightblue/on.gif"]
{
    content: url(http://gideonsw.nl/Plaatjes/GNP.png);
}
img[src*="http://www.gmot.nl/Themes/dilbermc/images/lightblue/new_some.gif"] {
    content: url(http://gideonsw.nl/Plaatjes/GNP.png);
  width: 30px;
  height: 30px;
}
img[src*="http://www.gmot.nl/Themes/dilbermc/images/lightblue/off.gif"] {
    content: url(http://gideonsw.nl/Plaatjes/GNNP.png);
}
img[src*="http://www.gmot.nl/Themes/dilbermc/images/lightblue/new_none.gif"] {
    content: url(http://gideonsw.nl/Plaatjes/GNNP.png);
  width: 30px;
  height: 30px;
}

.newicon a:link, .newicon a:visited{border-radius: 3px;}

select[name=searchtype] {
    width: 200px; 
}
table.tborder tbody  {
    width: 800px !important;    
}
.catbg2 a, .catbg a, .catbg3 a {
   color: #2b2b2b !important;
}
.windowbg, .windowbg3 {
    background: #A9ACB0 !important;
}
.windowbg2{
    background: #D8D9DA !important;    
}
.titlebg3, tr.titlebg2, .titlebg {
    background: #47525C !important;
}

.tborder {
   border: 0 !important;
   margin-top: 5px;
}
.catbg , tr.catbg td , .catbg3 , tr.catbg3 td {

    background: #596876 !important;
	color: #2b2b2b !important;
}
.catbg2 , tr.catbg2 td
{
    background: #596876 !important;
	color: #2b2b2 !important;
}
/* Pastebin */
div.embedPastebin { max-height: 600px; }
/* Dilber Theme*/
.maindiv
{
	background: gray;
    border: 0;
}

.topbar
{
    display: none;
}
.mainpage
{
	background: #D8D9DA;
    padding-top: 100px;
}


.topbar2
{
	background: #596876;
    z-index: 1;
    width: 900px !important;
}
.menubar
{
	background: blue;
	color: #FFFFFF;
    border-bottom: 5px solid #D8D9DA;
  z-index: 1;
  width: 900px;
}
.menubg , tr.menubg td
{
	background: #596876;
	color: #FFFFFF;
}

.menubg2 , tr.menubg2 td
{
	background: #D8D9DA;
	color: #FFFFFF;
}
.menubg a:link, .menubg a:visited , .menubg2 a:link, .menubg2 a:visited 
{
	color: #FFFFFF;
}
.menubg a:hover, .menubg2 a:hover
{
	color: #E0E0FF;
}

.memberbardiv
{
    background: #A9ACB0; 

}

.memberbardivbottom
{
	display: none;
}
/* Normal, standard links. */
a:link
{
	color: #2b2b2b;
}
a:visited
{
	color: #2b2b2b;
}
a:hover
{
    text-decoration: underline;
}
/* The main body of the entire forum. */
body
{
	background-color: #596876;
}
/* Standard horizontal rule.. ([hr], etc.) */
hr, .hrcolor
{
	color: gray;
	background-color: gray;
}


/* A quote, perhaps from another post. */
.quote
{
	color: #000000;
	background-color: #D8D9DA;
	border: 1px solid #000000;
}

/* A code block - maybe even PHP ;). */
.code
{
	color: #000000;
	background-color: #EEEEEE;
	/* Put a nice border around it. */
	border: 1px solid #000000;
}

/* The "Quote:" and "Code:" header parts... */
.quoteheader, .codeheader
{
	color: #000000;
}


/* These are used primarily for titles, but also for headers (the row that says what everything in the table is.) */
.titlebg, tr.titlebg th, tr.titlebg td, .titlebg2, tr.titlebg2 th, tr.titlebg2 td
{
	background: #596876;
}
.titlebg, .titlebg a:link, .titlebg a:visited
{
	color: black;
}

.titlebg a:hover
{
	color: #404040;
}
/* same as titlebg, but used where bold text is not needed */
.titlebg2 a:link, .titlebg2 a:visited
{
	color: black;
}
/* This is used for tables that have a grid/border background color (such as the topic listing.) */
.bordercolor
{
	background-color: #D8D9DA;
}
/* This is used on tables that should just have a border around them. */
.tborder
{
	border: 1px solid #6699CC;
	background-color: #FFFFFF;
}



/* Sometimes there will be an error when you post */
.error{
	color: red;
}
}
