css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
  domain("guardian.co.uk"),
  domain("theguardian.com")
{
  
  div.ad-slot,
  div.top-banner-ad-container,
  div.social-wrapper,
  div.meta__social,
  div.selection-sharing,
  .superlist-heading-item .social-buttons,
  body > div.site-message,
  body > div.breaking-news,
  iframe.email-sub__iframe,
  
  section#football100,
  section#guantÃ¡namo-diary,
  section#je-suis-charlie,
  section#watch-me-date,
  section#university-guide-2016,
  section#keep-it-in-the-ground,
  section#keep-it-in-the-ground-podcast,
  section#university-guide,
  section.fc-container--thrasher,
    
  
  .facia-page[role="main"] section#take-part,
  .facia-page[role="main"] section#watch-me-date,
  .facia-page[role="main"] section[id*="re-reading"],
  
  
  header#header > div.navigation-container div.l-header-pre,
  xfooter,
    
  body > div.site-message,
  
  xarticle#article .right-most-popular,
  
  article#article div.content__main-column--article + .content__secondary-column,
  article#article div.content__article-body > aside.element-rich-link,
  article#article div.content__article-body + .submeta div[data-component="keywords"] ~ *,
  article#article div.content__article-body + .submeta .u-cf,
  article#article + div.content-footer > *[class*="facia-container"],
  article#article + div.content-footer > *[class*="related"],
  article#article + div.content-footer > *[data-link-name*="fronts-on-article"],
  article#article + div.content-footer > *[class*="outbrain"],
  article#article + div.content-footer > *[data-component*="most-popular"],
  article#article + div.content-footer #comments div.discussion__top-comments,
    
  article#live-blog div.content__main-column--article + .content__secondary-column,
  article#live-blog div.content__article-body > aside.element-rich-link,
  article#live-blog + div.content-footer > *[class*="facia-container"],
  article#live-blog + div.content-footer > *[class*="related"],
  article#live-blog + div.content-footer > *[data-component*="most-popular"],
  article#live-blog + div.content-footer #comments div.discussion__top-comments

  {
    display: none !important;
  }
  
  .fc-item__meta
  {
    display: none !important;
  }
  
  article#article > header > div.tonal__standfirst,
  article#live-blog> header > div.tonal__standfirst
  {
    display: none !important;
  }

  /* Unstick sticky header */ 
  /*
  #header
  {
    position: absolute !important;
    top: 0 !important;
    margin-top: 0 !important;
    width: 100% !important;
  }
  
  #maincontent
  {
    margin-top: 110px !important;
  }
  */
  
  #header
  {
    margin-top: 0px !important;
  }
  
  #maincontent:empty
  {
    display: none !important;
  }


  
  

  
  /* desktops and tablets */
  @media screen and (min-device-width: 768px)
  {
    
    /*
    #maincontent
    {
      margin-top: 150px !important;
    }
    */

    .fc-item--has-metadata .fc-item__content
    {
      padding-bottom: 5px !important;
    }

    .has-flex-wrap .fc-item
    {
      min-height: 90px !important;
    }

    article#article
    {
      padding-bottom: 100px !important;
    }

    article#article > header > div.tone-background .content__headline,
    article#article > header > div.tone-background .content__headline--byline a,

    article#article > header > div.tonal__header .content__headline,
    article#article > header > div.tonal__header .content__headline .authorx,
    
    article#live-blog > header > div.tone-background .content__headline,
    article#live-blog > header > div.tone-background .content__headline--byline a,

    article#live-blog > header > div.tonal__header .content__headline,
    article#live-blog > header > div.tonal__header .content__headline .authorx
    
    {
      font-size: 2.0rem !important;
      line-height: 2.2rem !important;
    }

    article#article h1.content__headline,
    article#live-blog h1.content__headline
    {
      padding-bottom: 2.2rem !important;
    }
    
    article#article .content__article-body > p,
    article#article .content__article-body blockquote p,
    article#article .content__article-body > ul li
    {
      font-size: 1.0em !important;
      line-height: 1.4em !important;
    }

    .d-comment__body
    {
      font-size: 1.0em !important;
      line-height: 1.4em !important;
    }
  }
  
}
