css:
@-moz-document domain(reddit.com) {

/* 
R-Simple
Made by:
reddit usernmae: /u/whale
userstyles username: oap
Version: 1.1 (Feb 1 2015)
*/

body {
  font-family: 'Open Sans', sans-serif;
  font-weight: 300;
}

body.with-listing-chooser .listing-chooser .grippy:before {
  display: none;
}
body.with-listing-chooser .listing-chooser .grippy {
  display: none;
}
body.with-listing-chooser .listing-chooser {
  display: none;
}
body >.content .link .rank, .rank-spacer {
  display: none;
}
#header {
  background-color: #eae7e7;
  border-bottom: 5px solid #eae7e7;
}
#sr-header-area {
  border-bottom: none;
  background-color: #fbf8f8;
  text-transform: none;
  font-size: 100%;
}
.tabmenu li {
  margin: 0;
}
.tabmenu li a {
  background-color: #fbf8f8;
  padding: 5px;
}
.tabmenu {
  margin-top: 0px;
}
.tabmenu li.selected a {
  color: #e10000;
  border: none;
  border-bottom: none;
}      
a {
  color: black;
  font-weight: 400;
}
a:hover {
  color: grey;
}
.linkinfo {
  background-color: #fff;
  border: 1px solid grey;
  border-radius: 0;
}
.thing .title {
  color: #e10000;
  font-weight: 300;
}
.link:visited, .title:visited {
  color: black;
  font-weight: 400;
  opacity: 0.2;
}
li.share {
  display: none;
} 
.domain {
  display: none;
}  
#header-img.default-header {
  background: url(http://i.imgur.com/0NUhXgC.png);
  width:37px;
  margin-bottom: 3px;
  background-repeat: no-repeat;
  margin-left: 8px;
  margin-top: 8px;
  margin-right: 2px;

}
.tagline {
  color: grey;
}
.tagline a {
  color: grey;
}
#userbarToggle {
  display: none;
}
#header-bottom-right {
  background: #eae7e7;
  padding: 0;
}
.morelink {
  background: none;
  border: 1px solid grey;
  color: #000000;
}
.morelink .nub {
  display: none;
}
  .morelink:hover {
  background: none;
} 
.morelink a {
  color: grey;
  font-weight: 300;
}
.morelink:hover a {
  color: black;
}
.arrow.up {
  background: url(http://i.imgur.com/aVQv7w3.png);
  background-repeat: no-repeat;
}
.arrow.down {
  background: url(http://i.imgur.com/RKmN8e5.png);
  background-repeat: no-repeat;
}
.arrow.upmod {
  background: url(http://i.imgur.com/MDq24a8.png);
  background-repeat: no-repeat;
}
.arrow.downmod {
  background: url(http://i.imgur.com/yn8OEI3.png);
  background-repeat: no-repeat;
}
.arrow {
  height: 10px;
}
}
