css:
@-moz-document url-prefix("http://www.grafikart.fr") {
.header {
  background-color: #404852!important;
  color: white;
}

.grey {
  background-color: #808080;
}
.forum-post_body {
  background-color: #242424;
  -webkit-box-shadow: 0 4px 4px rgba(0,0,0,0.56);
  box-shadow: 0 4px 4px rgba(0, 0, 0, 0.56);
  border-color: #ADADAD;
  color: rgb(187, 187, 187);
}

.breadcrumb, .forum-actions {
  color: #DADADA;
}

.mdeditor_body {
  background-color: rgba(0, 0, 0, 0.64);
}

.cm-s-neo.CodeMirror {
  color: #C2C2C2;
}

.footer {
  color: lightgray;
  background-color: #424242;
}

.mdeditor_toolbar{
background: rgba(0, 0, 0, 0.81);
}

.is-answer {
	color:lightgrey!important;
}

.footer_title{
  color: #2a2f35;
}

.table,.sommaire_item {
  background-color: #565656;
}

.table tr:nth-child(2n),.sommaire_item:nth-child(2n)  {
  background-color: #969696;
}

body{
	color: #FFFFFF;
  	background-color:#808080;
}

.topic_meta {
  color: #E0E0E0;
}

.paginate a, .paginate span {
	background-color:#272727;
}

.comment, .form-control {
  background-color: #2D2D2D;
}

.form-control {
  background-color: #2D2D2D;
  color:lightgrey!important;
}

.form-control:focus {
	background-color: rgb(174, 174, 174);
}

::-webkit-input-placeholder { /* WebKit browsers */
    color:    #fff;
}
:-moz-placeholder { /* Mozilla Firefox 4 to 18 */
   color:    #fff;
   opacity:  1;
}
::-moz-placeholder { /* Mozilla Firefox 19+ */
   color:    #fff;
   opacity:  1;
}
:-ms-input-placeholder { /* Internet Explorer 10+ */
   color:    #fff;
}

.formation, .formation_image, .theme, .notifications, .tutoriel, .badge-modal_box, .forum-bloc, .forum-bloc-mono, .formations-last_content, .path {
  background-color: #2A2929;
}
  
.formations-last_item_title,.formation_title {
	color:lightgrey;
}

.formations-last_item_count, .formations-last_item_date {
	color:#888;
}
    
.sommaire span.sommaire_label,.sommaire_infos {
	color:white;
}
  
  
    
}
