css:
@-moz-document domain("play.pokemonshowdown.com") {

  .mainmenufooter small:after{
    content:"Made by: Charles Paroissien"; 
    font-size:9pt; 
    color:#cccccc; 
    text-shadow:0 1px black; 
    position:absolute; 
    size:6px; 
    left:230px; 
    top:21px; 
    width: 300px;
  }

  /* Interface */

  body { 
    background:url(http://i.imgur.com/THQubkt.jpg?2) no-repeat !important;
  }
  

  
    .broadcast-red {
    background: none repeat scroll 0% 0% #EC5C4D;
    color: #000;
    border: 2px solid #000;
    padding: 5px;
    font-family: Futura,"Trebuchet MS",Arial,sans-serif;
    font-size: 10pt;
}
  
  .broadcast-blue {
    background: none repeat scroll 0% 0% #627ADC;
    color: #000;
    border: 2px solid #000;
    padding: 5px;
    font-family: Futura,"Trebuchet MS",Arial,sans-serif;
    font-size: 10pt;
}
  
  .broadcast-green {
    background: none repeat scroll 0% 0% #9BB38F;
    color: #000;
    border: 2px solid #000;
    padding: 5px;
    font-family: Futura,"Trebuchet MS",Arial,sans-serif;
    font-size: 10pt;
}
  
a {
  color: #5C43FF;
}
  
  .mainmenu .menugroup:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button {
    box-sizing: border-box;
    font-size: 11pt;
    font-family: Verdana,Helvetica,Arial,sans-serif;
    display: inline-block;
    padding: 5px 7px 3px;
    width: 240px;
    outline: medium none;
    cursor: pointer;
    text-align: center;
    text-decoration: none;
    text-shadow: 0px -1px 0px #0F1923;
    border-radius: 5px;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.2), 0px 1px 1px rgba(255, 255, 255, 0.8) inset;
    color: #FFF;
    border: 3px solid #3D668E;
    background: #627ADC;
  }
  .mainmenu .menugroup:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover {
    border: 3px solid #3D668E;
    background: #526EDC;
  }

.mainmenu .menugroup:nth-child(1) .button {
    width: 230px;
    padding: 6px 7px;
    font-size: 13pt;
}
  
  .mainmenufooter a {
    color: #BF85C9;
    text-shadow: 0px 0px 1px #B23BC4, 0px 0px 1px #B23BC4, 0px 0px 1px #B23BC4;
}
  
  .mainmenufooter a:hover {
    color: #BE6FCA;
}
  
  .header .logo {
    opacity: 0;
  }
  
  .pm-window h3 {
    background: #627ADC;
    color: #000;
    margin: 0px;
    padding: 2px 5px;
    font-size: 11pt;
    border: 2px solid #000;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    cursor: pointer;
}
  
  .pm-window h3:hover {
    color:#FFB049;
  }
  
  .pm-window.focused h3, .pm-window.focused h3:hover {
background: #627ADC;
border-color: #000;
    color:#FFB049;
}
  
  .pm-log {
    border: none;
    background: url("http://i.imgur.com/Q8vHT0Y.png") repeat scroll 0% 0% transparent;
opacity: 0.9
  }
  
  .pm-window h3 small {
    color:#303030;
  }
  
  .roomlist a.ilink {
    color: #627ADC;
    border: 1px solid black;
  }
  
  .roomlist a.ilink:hover {
    color: #546FDB;
    border: 1px solid black;
  }
  
  .maintabbarbottom {
    background: #FFFFF2;
    border-color: #000;
  }
  
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    background: #FFFFF2;
      border-color:#000;
    color: #000;
  }
  
  .tabbar a.button {
    background:#FFFFFD;
    color:#000;
}

.tabbar a.button:hover {
    background:#FFFFF2;
}
  
  .tournament-tools {
    padding: 10px;
    display: none;
    border-top: 1px solid #AAA;
    background: url("http://i.imgur.com/Q8vHT0Y.png") repeat scroll 0% 0% transparent;
    opacity: 0.7;
}
  
  .tournament-bracket {
    font-size: 8pt;
    background: url("http://i.imgur.com/Q8vHT0Y.png") repeat scroll 0% 0% transparent;
    opacity: 0.8;
}
  
  .tournament-title {
    font-weight: bold;
    cursor: pointer;
    background: #627ADC;
    border:1px solid black;
}
  
  .tournament-status {
    left: 0px;
    background: #D4A5DB;
    color: #FFF;
}
  
  .tournament-toggle{
    background: #D4A5DB;
    color: #FFF;
}
  
  .ps-popup {
    background: url("http://i.imgur.com/Q8vHT0Y.png");
    opacity: 0.95;
    border: 1px solid black;
  }
  
  .setchart-nickname {
    background: #627ADC;
  }
  
  .ps-room .battle-log {
        background: url("http://i.imgur.com/Q8vHT0Y.png");
  }
  
  .ps-room.ps-room-opaque {
    background: url("http://i.imgur.com/Q8vHT0Y.png");
  }
  
  .battle-log h2 {
    background: #627ADC;
    border:1px dashed black;
  }
  
  .message-pm {
    color: #BF85C9;
}
  
  .popupmenu h3 {
    margin: 5px 0px 0px -2px;
    padding: 1px 0px;
    font-size: 8pt;
    color: #627ADC;
}
  
.popupmenu button:hover {
    display: block;
    cursor: pointer;
    font-size: 8pt;
    font-family: Verdana,Helvetica,Arial,sans-serif;
    margin: 0px 0px 0px 6px;
    padding: 0px 4px;
    border: 0px none;
    border-radius: 2px;
    background: #BF85C9;
    color: #FFF;
    width: 204px;
    height: 19px;
    text-align: left;
    box-sizing: border-box;
}
  
  .popupmenu button.sel:hover{
    padding: 0px 3px;
    border: 1px dotted #000;
    background: #BF85C9;
    color: #FFF;
  }
  
  
  .popupmenu button.sel {
    padding: 0px 3px;
    border: 1px dotted #000;
    background: none;
}
  
  .pm-log-add {
    border: 1px solid black;
  }
  
  .textbox:hover,
  .textbox,
  .textbox:focus {
    border: 1px solid black;
  }
  
  .battleform label {
 background: url("http://i.imgur.com/yFWeHx0.png") no-repeat scroll left center transparent !important;
  padding: 4px;
	opacity:0.9;
}
  
  .menugroup {
    display: block;
    padding: 10px 0px;
    margin: 0px 27px 30px;
    max-width: 400px;
    border-radius: 20px;
    background: none;
    text-shadow: 0px 1px 0px #000;
}
  
  .battle-log .rated strong {
    color: #FFF;
    background: #BF85C9;
    padding: 1px 4px;
    border-radius: 4px;
}
  
  
  .challenge {
    margin-top: -1px;
    background: rgba(255, 255, 255, 0.7);
    border: 1px solid #000;
    color: #E12727;
    padding: 1px 5px;
}
  
  .label {
    color: #FFF;
  }
  
  .challenge p {
    color: #000;
  }

  .select {
    cursor: pointer;
    box-sizing: border-box;
    width: 230px;
    height: 32px;
    padding: 0px 5px;
    font-size: 9pt;
    text-align: left;
    font-family: Verdana,Helvetica,Arial,sans-serif;
    border-radius: 4px;
    border: 1px solid #000;
    background: none repeat scroll 0% 0% #EEE;
    box-shadow: 0px 1px 0px #FFF inset;
    color: #000;
    border-top-left-radius:0px;
}
  
  .select.preselected, .select.preselected:disabled {
    color: #000;
    border: 1px solid #000;
}
  
  .userlist li button:hover {
    background: rgba(0, 0, 0, 0.1);
  }
  
  button {
    border: 1px solid black;
  }
  
  .teamchart li.format-select select {
    font: 12pt Verdana,sans-serif;
    border: 1px solid #000;
}
  
  .setchart {
    border: 1px solid #000;
  }
  
  .ps-room.ps-room-light {
    border-color: black;
}
  
  .teamchartbox {
    border-top: 1px solid black;
  }
  
  .setchart input:hover, .setchart-nickname input:hover, .statform input.inputform:hover, .textbox:hover,
  .setchart input, .setchart-nickname input, .statform input.inputform, .textbox,
  .setchart input:focus, .setchart-nickname input:focus, .statform input.inputform:focus, .textbox:focus {
    outline: 0px none;
    border: 1px solid #000;
    box-shadow: none;
    background: none repeat scroll 0% 0% #FFF;
}
  
  .setstats {
    border: 1px solid #000;
    box-shadow: none;
  }
  
  .setdetails, .setstats,
  .setdetails:focus, .setstats:focus {
    border: 1px solid #000;
    margin: 1px 1px 0px 0px;
    outline: 0px none;
    box-shadow: none;
}
  
  
  .teambar button {
    border: 1px solid #000;
  }
  
.teambar button:disabled, .teambar button:disabled:hover, .teambar button:disabled:active {
    color: #000;
    background: none repeat scroll 0% 0% #FCFCFC;
    border-color: #000;
    opacity: 1;
    cursor: default;
}

  .ps-room .battle-controls {
    background: transparent !important;
  }

  
}
