css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("wikipedia.org") {

/*レイアウト変更*/
#mw-head,#mw-panel,div#footer {
display:none !important;
}
.mw-body {
margin: -75px auto 0 !important;
width: 600px !important;
font-size: 1.3em !important;
border: #A7D7F9 solid 1px;
}

body,#mw-page-base,#mw-head-base,#content,#footer-info-viewcount,
#footer-info-lastmod,#toc,#preferences,#p-namespaces,table.ambox,div.thumbinner,
.catlinks,.infobox,.navbox,.navbox-subgroup,.navbox-even,
html.client-nojs body.mediawiki div#content.mw-body div#bodyContent.mw-body-content div#mw-content-text.mw-content-ltr div div
,html.client-nojs body.mediawiki div#content.mw-body div#bodyContent.mw-body-content div#mw-content-text.mw-content-ltr table tbody tr td
,pre, .mw-code,.navbox-title, table.navbox th,div#footer ul li,.toccolours,table,.fileinfo-paramfield
,.infobox,.pathnavbox,.navbox-subgroup .navbox-group,td,div[style*="background"],th[style*="background"],td[style*="background"] {
background: #0d0015 !important;
color: #d0d0d0 !important;
}
div.vectorTabs ul {
background:none !important;
}
div.vectorTabs li.selected {
background:none !important;
}
div.vectorTabs ul li {
background:none !important;
    background-color: #111 !important;
}
#pagehistory li.selected {
    background-color: #111 !important;
}
div.vectorMenu ul {
background:none !important;
}
#preftoc li.selected a {
background:none !important;
}
.skin-vector .mw-notification,.mw-search-formheader{
    background-color:#111 !important;
}
.editOptions,ul#filetoc,table.mw_metadata td {
    background-color:#111 !important;
}
table.wikitable,table.wikitable > tr > th, table.wikitable > * > tr > th,
.mw-datatable td,table.mw_metadata th,div#simpleSearch,div[style*="background"] {
    background-color:#111 !important;
    color: #f0f0f0 !important;
}
textarea {
    background-color:#d0d0d0 !important;
}

/*
h1,h2,h3,h4,h5,h6 {
color: #2ecc40 !important;
font-weight: bold !important;
}
*/

h1 {color: #e0ebaf !important;;}
h2 {color: #d8e698 !important;}
h3 {color: #c7dc68 !important;}
h4 {color: #99ab4e !important;}
h5,h6 {color: #7b8d42 !important;}

#mw-navigation h1 {color: #e0ebaf !important;}
#mw-navigation h1 a:link {color: #e0ebaf !important;}
#mw-navigation h2 {color: #d8e698 !important;}
#mw-navigation h2 a:link {color: #d8e698 !important;}
#mw-navigation h3 {color: #c7dc68 !important;}
#mw-navigation h3 a:link {color: #c7dc68 !important;}
#mw-navigation h4 {color: #99ab4e !important;}
#mw-navigation h4 a:link {color: #99ab4e !important;}
#mw-navigation h5,h6 {color: #7b8d42 !important;}
#mw-navigation h5,h6 a:link {color: #7b8d42 !important;}

b {
display: inline-block !important;
color:#ec6d51 !important;
font-size: large !important;
padding:3px 2px !important;
}
a:link {
color: #ffdc00 !important;
font-weight:bold;
}
#preferences legend {
    color: #fafafa !important;
    font-size: small !important;
}
.htmlform-tip {
    color: #30d8ff !important;
        font-size: small !important;
}
hr {
color: #ffdc00 !important;
}
body {
font-size:100% !important;
}
#mw-content-text dt {color: #83ccd2 !important;}
ol.references > li:target {
    background-color: #333 !important;
}
}
