css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("audi80.pl") {

/*BOX BACKGROUND COLOR*/

td.row2 {
  background-color: #E9E9E9;
}
 
td.row1 {
  background-color: #E9E9E9;
}
  
  td.row3Right, td.spaceRow {
    background-color: #D1D7DC;
    border: medium solid #D1D7DC;
}

  p, td			{ font-size : 11px; color : #000000; } 

  body {
background-color:#7E7E7E;
    background-image: none;
    background-repeat: repeat;
    margin: 0px;
}
  td.row_helped {
    background-color: #CBCBCB;
}
  
  td.row4 {
    background-image: url("http://i1244.photobucket.com/albums/gg567/albi888/newrow1_zps4776d751.jpg");
    border: 1px solid #9F5151;
    padding: 4px;
}
  .topictitle,h1,h2	{ font-weight: bold; font-size: 11px; color : #000000; }
a.topictitle:link	{ text-decoration: none; color : #000000; }
a.topictitle:visited	{ text-decoration: none; color : #5493B4; }
a.topictitle:hover	{ text-decoration: underline; color : #D72424; }
  .pagination {
    color: #D8D8D8;
    font-size: 10px;
    font-weight: bold;
    padding: 3px 3px 4px;
    cursor: pointer;
    text-decoration: none;
}
  a.nav {
    text-decoration: none;
    color: #000;
}
.gen, .genmed, .gensmall {
    color: #000;
}
  
  #submenu {
    width: auto;
    background-color: #F0F0F0;
    padding: 5px;
    text-align: right;
    float: right;
    border: 1px solid #000;
}
  .postdetails {
    font-size: 10px;
    color: #433;
}
  .bodyline {
    background-color: #FFF;
}
  td.quote {
    font-family: Verdana,Arial,Helvetica,sans-serif;
    font-size: 11px;
    color: #444;
    line-height: 125%;
    background-color: #FAFAFA;
    border: 1px solid #D1D7DC;
}
.code {
font-family:		Courier, 'Courier New', sans-serif; font-size: 11px; color: #006600;
background-color:	#FAFAFA; border: #D1D7DC; border-style: solid;
border-left-width:	1px; border-top-width: 1px; border-right-width: 1px; border-bottom-width: 1px;
} 
  a:link, a:active, a:visited, a:active, a:visited  {text-decoration:none;color: #9b1111;}
  a:hover {text-decoration: underline;color: #9b1111} 
  a.gen, a.genmed, a.gensmall {color: #000;}
  a.gen,a.gen:hover,a.genmed:hover,a.gensmall:hover	{ color: #000; text-decoration: underline; }
  a.forumlink, a.forumlink:active, a.forumlink:visited {text-decoration: none; color: #000080}
  a.forumlink:hover	{ text-decoration: underline; color: #000080 }
  a.mainmenu:hover	{ text-decoration: none; color : #EAEAEA;}
  a.menu,a.menu:visited,a.menu:active {text-decoration: none; color: #EAEAEA;}
  a.menu:hover	{ text-decoration: underline; background-image:	url(http://www.audi80.pl/templates/audi/images/menu_05.jpg);}
  a.postlink:hover	{ text-decoration: underline; color : #3300FF; }
  a.nav, a.nav:active, a.nav:visited	{ text-decoration: none; color : #000033; }
  a.nav:hover	{ text-decoration: underline; }
  
  a.topictitle:link {text-decoration: none;color: #000;}
  
  
  .topictitle,h1,h2	{ font-weight: bold; font-size: 11px; color : #000000; }
a.topictitle:link	{ text-decoration: none; color : #000000; }
a.topictitle:visited	{ text-decoration: none; color : #5493B4; }
a.topictitle:hover	{ text-decoration: underline; color : #D72424; } 
  
  input.post, textarea.post, select            { background-color : #c5ccd2; border: solid 1px #000;}
  input,textarea, select {color : #000;font: normal 11px Verdana, Arial, Helvetica, sans-serif;border: solid 1px #000;}
  input.button {background-color :	#DEE3E7;color:#000;font-size:11px; font-family: Verdana, Arial, Helvetica, sans-serif;cursor:pointer;}
  element {width: 550px;background-color: #DEE3E7;}
  
}
