css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(brb.to) {
    body, .m-theme-video .l-content {
        background-image: none;
    }

    body {
        background: #FAFAFA
    }

    .b-poster-tile__image, .b-poster-new__image-poster, .b-tab-item .poster-main {
        outline: 1px solid #DEDEDE;
        border: 1px solid transparent;
        box-shadow: none;
    }

    .b-poster-tile__link:hover {
        opacity: 0.95;
    }
}

@-moz-document domain(fs.to) {
    div.m-main {
        background: #FAFAFA
    }
    .l-content-wrap {
        box-shadow: 0px 0px 4px 1px #DDD;
    }

    .b-header__inner {
        width: 990px;
    }

    .l-content-center {
        float: left;
        width: 960px;
        padding: 0px 7px 0px 0px;
    }

    .b-item-material-comments__item-right {
        float: left;
        margin: 2px 0px 0px 14px;
        width: 879px;
        overflow: hidden;
    }

    .b-item-material-comments__footer {
        padding: 0;
    }

    .l-content {
        border-radius: 0;
    }

    .b-header__menu-subsections-wrap {
        padding-top: 2px;
        top: 36px;
        opacity: 0.95;
    }

    .l-header__wrap div {
        background: none;
        box-sizing: content-box;
    }

    .l-header, .b-header__menu-subsections {
        border-radius: 3px 3px 0 0;
        background: #2b4d70 !important;
        box-shadow: 0px 0px 6px 2px #888 !important;

    }

    .b-header__menu-subsections {
        box-shadow: 0px 0px 0px 0px #888 !important;
    }

    .l-header .b-button-header__link *, .l-header .b-button-header__link, .b-button-header {
        background: none;
    }

    .l-header .b-button-header__link {
        border-radius: 3px;
        border: 1px solid #B6D0E3;
        margin-right: 4px;
    }
    .l-header .b-button-header__link span, .l-header .b-button-header__link {
        height: 22px;
        padding: 0;
        line-height: 19px;
        min-height: 22px;
    }

    .l-header .b-button-header__link {
        margin-top: 5px;
        padding: 0 12px;
    }

    .l-header #search {
        margin-top: 2px;
        border-color: #A0CFE2;
        background: #E6F4FF;
        height: 24px;
    }

    .b-main__top-commentable-item-wrap {
        border-radius: 0px;
    }

    .b-main__top-commentable-item-wrap:hover {
        border-radius: 0px;
        box-shadow: 0px 0px 4px 1px #DDD;
    }

    .b-main__title {
        margin: 0;
    }

    body, .m-theme-video .l-content {
        background-image: none;
    }

    body {
        background: #FAFAFA
    }

    .b-poster-tile__image, .b-poster-new__image-poster, .b-tab-item .poster-main {
        outline: 1px solid #DEDEDE;
        border: 1px solid transparent;
        box-shadow: none;
    }

    .b-poster-tile__link:hover {
        opacity: 0.95;
    }

    .b-scroll-top {
        border-radius: 0;
        height: 48px;
        width: 32px;
        box-shadow: 0px 0px 4px 1px #AAA !important;
    }

    .l-content.m-content_type_player {
        min-height: 200px !important;
        padding-bottom: 0 !important;
    }

    .b-pager ul li a {
        box-shadow: none;
    }

    div.m-main {
        background: #FAFAFA
    }
    .l-content-wrap {
        box-shadow: 0px 0px 4px 1px #DDD;
    }

    .l-content {
        border-radius: 0;
    }

    .l-header__wrap div {
        background: none;
        box-sizing: content-box;
    }

    .l-header, .b-header__menu-subsections {
        border-radius: 0 0 3px 3px;
        background: #2b4d70 !important;
        box-shadow: 0px 0px 6px 2px #888 !important;

    }

    .b-header__menu-subsections {
        box-shadow: 0px 0px 0px 0px #888 !important;
    }

    .l-header .b-button-header__link *, .l-header .b-button-header__link, .b-button-header {
        background: none;
    }

    .l-header .b-button-header__link {
        border-radius: 3px;
        border: 1px solid #B6D0E3;
        margin-right: 4px;
    }
    .l-header .b-button-header__link span, .l-header .b-button-header__link {
        height: 22px;
        padding: 0;
        line-height: 19px;
        min-height: 22px;
    }

    .l-header .b-button-header__link {
        margin-top: 5px;
        padding: 0 12px;
    }

    .l-header #search {
        margin-top: 2px;
        border-color: #A0CFE2;
        background: #E6F4FF;
        height: 24px;
    }

    .b-main__top-commentable-item-wrap {
        border-radius: 0px;
    }

    .b-main__top-commentable-item-wrap:hover {
        border-radius: 0px;
        box-shadow: 0px 0px 4px 1px #DDD;
    }

    .b-main__title {
        margin: 0;
    }
    .b-scroll-top {
        border-radius: 0;
        height: 48px;
        width: 32px;
        box-shadow: 0px 0px 4px 1px #AAA !important;
    }

    .l-content.m-content_type_player {
        min-height: 200px !important;
        padding-bottom: 0 !important;
    }

    .b-pager ul li a {
        box-shadow: none;
    }

}
