css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("arbetsmyra.dyndns.org") {



/* The defaults */
body {
	background-color: #010101;
	background-image: url(http://upload.wikimedia.org/wikipedia/commons/6/65/Black_screen_of_the_camera_2014-04-24_19-20.jpg);
	background-repeat: repeat-y;
	background-attachment: scroll;
	padding: 2px;
	margin: 2px;
}

p {
	margin-top: 0.33em;
	margin-bottom: 1em;
}


/* IE 5.0 don't accept width in "body",
   so we specify it here, in a real class. */
.mybody {
	font-family: Sans, Arial, Sans-serif, Helvetica;
	font-weight: normal;
	font-size: 10pt;
	color: green;
	text-align: left;
	padding: 2px;
	margin-left: 73px;
	margin-right: 1em;
	margin-top: 1em;
	margin-bottom: 1em;
	width: 90%;
}

/* Make the downloadpage wider. */
.bodydownload {
	font-family: Sans, Arial, Sans-serif, Helvetica;
	font-weight: normal;
	font-size: 10pt;
	color: green;
	padding: 0px;
	margin-left: 80px;
	margin-right: 1em;
	margin-top: 1em;
	margin-bottom: 1em;
	width: 52em;
}


/* Table of contents */
.table-of-contents {
	float: right;
	width: 24%;
	background: #111111;
	padding-top: 2px;
    padding-left: 2px;
    padding-bottom: 2px;
    padding-right: 2px;
	margin-top: 2em;
	margin-left: 2em;
	margin-bottom: 2px;
	margin-right: 2px;
	border-style: solid;
	border-width: 1px;
	-moz-border-radius: 13px 13px 13px 13px;
	border-radius: 13px 13px 13px 13px;

	-moz-box-shadow: 2px 2px 2px 0px #0d0;
	-webkit-box-shadow: 2px 2px 2px 0px #0d0;
	box-shadow: 2px 2px 2px 0px #0d0;

	font-weight: bold;
	font-size: 10pt;
}
.table-of-contents ul {
	margin-left: 1.5em;
	margin-bottom: 0px;
	font-weight: normal;
	font-size: 8pt;
}
.table-of-contents li {
	margin-bottom: 0px;
	font-weight: normal;
	font-size: 8pt;
}


/* Centered objects */
.center {
	text-align: center;
	vertical-align: middle;
	padding: 0px;
	margin: 0px;
}


/* No borders for images and
 * align verticaly. */
img {
	padding: 2px;
	margin-right: 0px;
	margin-left: 0px;
	margin-top: 0px;
	margin-bottom: 0px;
	border-style: none;
	vertical-align: baseline;
}



/* Use only small left margin in lists */
ul {
	margin-top: 0em;
	margin-bottom: 1em;
	margin-right: 0em;
	margin-left: 2em;
	padding: 0em;
}
ol {
	margin-top: 0em;
	margin-bottom: 1em;
	margin-right: 0em;
	margin-left: 2em;
	padding: 0em;
}
li {
	margin-top: 0.33em;
	margin-bottom: 0em;
	margin-right: 0em;
	margin-left: 0em;
	padding: 0em;
}




/* Thin gray lines */
hr {
	color: green;
	background-color: green;
	border: 0px;
	height: 1px;
	width: 75%;
	padding: 0px;
	margin-top: 2em;
	margin-bottom: 2em;
	text-align: center;
}


hr.narrow1 {
	color: green;
	background-color: green;
	border: 0px;
	height: 1px;
	width: 17em;
	padding: 0px;
	margin-top: 2em;
	margin-bottom: 2em;
	margin-left: 3.8em;
	text-align: left;
}



/* Headers */
H1 {
	font-weight: bold;
	font-size: 24pt;
	margin-top: 10px;
	margin-bottom: 0.5em;
	text-align: center;
}

H2 {
	font-weight: normal;
	font-size: 16pt;
	margin-top: 1em;
	margin-bottom: 0.5em;
	text-align: center;
}

H3 {
	font-weight: bold;
	font-size: 12pt;
	margin-top: 2em;
	margin-bottom: 0.5em;
}

H4 {
	font-weight: bold;
	font-size: 10pt;
	margin-top: 1em;
	margin-bottom: 0.5em;
}


/* Fixed spaced fonts */
PRE, code {
	font-family: 'Courier New', Courier, 'Lucida Console', sans, monospace;
	font-weight: normal;
	font-size: 8pt;
	margin-top: 0em;
	margin-bottom: 0em;
	padding: 0em;
}




/* Coding examples */
.codeFrame {
	font-family: 'Courier New', Courier, 'Lucida Console', sans, monospace;
	font-style: normal;
	font-size: 8pt;
	white-space: nowrap;
	background-color: #111111;
	border-style: solid;
	border-width: 1px;
	-moz-border-radius: 13px 13px 13px 13px;
	border-radius: 13px 13px 13px 13px;
	-moz-box-shadow: 2px 2px 2px 0px #0d0;
	-webkit-box-shadow: 2px 2px 2px 0px #0d0;
	box-shadow: 2px 2px 2px 0px #0d0;
	padding-left: 1.5em;
	padding-top: 0.33em;
	padding-bottom: 0.33em;
	margin-top: 0.33em;
	margin-bottom: 0.33em;
	margin-right: 65%;
}
 

.codeInline {
	font-family: 'Courier New', Courier, 'Lucida Console', sans, monospace;
	font-style: normal;
	color: #000000;
	background: #00ff00;
}



/* --------------------------------------------------------------------------*/
/* Make Nedstat counter pic to dissapear. */
#nedstat {
/*	width: 0px;
	height: 0px;
	padding: 0px;
	margin: 0px;*/
	border-style: none;
	vertical-align: middle;
}

/* Align caption with image */
#usbtousbcable {
	padding-left: 250px;
}

/* Right align image */
#raspipic {
	float: right;
	margin-top: 0.5em;
	margin-left: 2em;
	margin-bottom: 1em;
	margin-right: 2%;
}

/* Right align image */
#computemodulepic {
	float: right;
	margin-top: 1.5em;
	margin-left: -90px;
	margin-bottom: 2px;
	margin-right: 10px;
}

#picusbcardreader {
	padding-left: 4em;
}

/* Right align image */
#legalpic {
	float: right;
	margin-top: 4em;
	margin-left: 2em;
	margin-bottom: 10px;
	margin-right: 10%;
}

/* Shadow border */
#picthumb {
	-moz-box-shadow: 0px 0px 0px 0px #0d0;
	-webkit-box-shadow: 0px 0px 0px 0px #0d0;
	box-shadow: 0px 0px 0px 0px #0d0;
}


/* Shadow border */
#picmakescan {
	-moz-box-shadow: 2px 2px 2px 0px #0d0;
	-webkit-box-shadow: 2px 2px 2px 0px #0d0;
	box-shadow: 2px 2px 2px 0px #0d0;
}


}
