css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wegenforum.nl") {
#quick-links a.responsive-menu-link {
    display: none;
}

#quick-links .dropdown.hidden {
    display: inherit;
    position: relative;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    border: none;
    padding: 0;
    margin-right: auto;
}

#quick-links .dropdown.hidden .pointer {
    display: none;
}

#quick-links .dropdown.hidden ul.dropdown-contents {
    background: none;
    border: none;
    box-shadow: none;
    padding: 0;
}

#quick-links .dropdown.hidden ul.dropdown-contents li.separator {
    display: none;
}

#quick-links .dropdown.hidden ul.dropdown-contents li {
    display: inline-block;
    padding: 0;
}

#quick-links .dropdown.hidden ul.dropdown-contents li.small-icon.icon-search a, #quick-links .dropdown.hidden ul.dropdown-contents li.small-icon.icon-members a, #quick-links .dropdown.hidden ul.dropdown-contents li.small-icon.icon-team a, #nav-main li.small-icon.icon-faq a {
    text-indent: -9999px;
}

#quick-links .dropdown.hidden ul.dropdown-contents li.small-icon.icon-faq a {
    display: none;
}


}
