css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('premierleaguepass.com') {
/* Write your CSS here. */
  .head-up-box   {display:none;}
  .showandhighlights {margin-top: 0 !important;}
  .showandhighlights .title {display: none;}
  .showandhighlights .content {padding-bottom: 0 !important;}
  .navigator {
    height: 40px !important;
    line-height: 40px !important;
  }
  .nav-list {
    line-height: 40px !important;
  }
  .logo {margin-top:0 !important;}
  .home-slide {display:none;}
  .full-schedule  {display:none;}
  #scoreBoardBox .title {padding: 5px 0px 1px 0px !important;}
  .footer-top-position {padding:0 !important;}
  #mainBg {min-height: 520px !important;}
  
  #footer .colume {
    font-size: 11px !important;
    line-height: 15px !important;
    padding-top: 5px !important;
    padding-bottom: 15px !important;
  }
  .colume .gaming {
    margin-bottom: 10px !important;
  }
  .footer-top-position .colume {padding-top: 10px !important;}
  .footer-top-position .colume:nth-child(1) {display:none !important;}
  .fullTimeInfo {color:#333333 !important; font-weight: bold !important;font-size:16px;}
  .timeInfoFinal {background-color:red !important; color:white !important;font-size:16px;}
  .timeInfoLive {background-color:green !important; color:white !important;font-size:16px;}
  .gameName p {color:#333333 !important; font-size:14px !important; font-weight: bold; text-transform:none  !important ;}
}
