css:
@-moz-document url-prefix("http://www.anime-sharing.com/forum/") {
/*d9d4dd,613D7A, 261830, 432a55, nav 301E3D, border 3a2449*/
h2.searchlisthead{background: #613D7A url(http://images.anime-sharing.info/images/buttons/newbtn_middle.png) repeat-x!important;
  border: 1px solid #432a55!important;}
/*Forum title*/
.forumhead {
background: #613D7A url(http://i.imgur.com/AkR4ftu.png) repeat-x!important;
border: 1px solid #432a55!important;
}
body a {color: #432a55!important;}
#sidebar_container a:hover {color: #261830!important;}
#sidebar_container a {color: #432a55!important;}
div#google_ads_iframe{dispaly:none!important}
.smallavatartext.widget_post_comment:nth-child(odd) {
background-color: floralwhite!important;
}
/*li.avatarcontent.floatcontainer.widget_post_bit {}
.smallavatartext.widget_post_comment{}*/
li.avatarcontent.floatcontainer.widget_post_bit:nth-child(odd){background-color: floralwhite!important;}

.postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead {
background: #613D7A url(http://images.anime-sharing.info/images/buttons/newbtn_middle.png) repeat-x!important;
border: 1px solid #613D7A!important;
}

.navbar {background: #613D7A none!important;}
.newcontent_textcontrol:hover {
background: #261830 url(http://images.anime-sharing.info/images/buttons/newbtn_middle.png) repeat-x left top!important;}

body a:hover, body a:hover .time, body a:hover .shade, body a:hover .understate {
color: #613D7A!important;}

.newcontent_textcontrol {
background: #613D7A url(http://images.anime-sharing.info/images/buttons/newbtn_middle.png) repeat-x left top!important;}
.blockhead {background: #613D7A url(http://i.want.tf/to/that/ksxRbo5w.png) repeat-x!important;border: 1px solid #613D7A!important;}
.breadcrumb .crust:hover a.crumb {color: #613D7A;}
.pagination span.selected a {background: #613D7A url(http://images.anime-sharing.info/images/buttons/newbtn_middle.png) repeat-x!important; border: 1px solid #613D7A!important;}
/* pagination page*/
.pagination span a.popupctrl,.pagination span a:hover, .pagination span a{background-color:#613D7A!important;border: 1px solid #613D7A!important;}
.pagination span a:hover {border: 1px solid #613D7A!important;}
.pagination span a:hover,.pagination span a {color: white!important;}
/*nav*/
.navtabs li.selected a.navtab{background: #613d7a!important; /* Old browsers */
background: -moz-linear-gradient(top,  #613d7a 0%, #451360 49%, #451360 49%, #3a2449 52%, #613d7a 100%)!important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#613d7a), color-stop(49%,#451360), color-stop(49%,#451360), color-stop(52%,#3a2449), color-stop(100%,#613d7a))!important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top,  #613d7a 0%,#451360 49%,#451360 49%,#3a2449 52%,#613d7a 100%)!important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top,  #613d7a 0%,#451360 49%,#451360 49%,#3a2449 52%,#613d7a 100%)!important; /* Opera 11.10+ */
background: -ms-linear-gradient(top,  #613d7a 0%,#451360 49%,#451360 49%,#3a2449 52%,#613d7a 100%)!important; /* IE10+ */
background: linear-gradient(to bottom,  #613d7a 0%,#451360 49%,#451360 49%,#3a2449 52%,#613d7a 100%)!important; /* W3C */
color:white!important;border-right: #613D7A 5px double!important; border-left: #613D7A 5px double!important;}
/*nav hover*/
.navtabs li a.navtab:hover {background: #613d7a!important; /* Old browsers */
background: -moz-linear-gradient(top,  #613d7a 0%, #451360 49%, #451360 49%, #3a2449 52%, #613d7a 100%)!important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#613d7a), color-stop(49%,#451360), color-stop(49%,#451360), color-stop(52%,#3a2449), color-stop(100%,#613d7a))!important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top,  #613d7a 0%,#451360 49%,#451360 49%,#3a2449 52%,#613d7a 100%)!important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top,  #613d7a 0%,#451360 49%,#451360 49%,#3a2449 52%,#613d7a 100%)!important; /* Opera 11.10+ */
background: -ms-linear-gradient(top,  #613d7a 0%,#451360 49%,#451360 49%,#3a2449 52%,#613d7a 100%)!important; /* IE10+ */
background: linear-gradient(to bottom,  #613d7a 0%,#451360 49%,#451360 49%,#3a2449 52%,#613d7a 100%)!important; /* W3C */
border-right: #332041 5px double!important; border-left: #332041 5px double!important;} 
.navtabs > ul.floatcontainer li a:hover {background: #333333!important;text-decoration: none;}
#forums a.username{color:#613D7A;}
#forums a.username:hover{color:#261830;}
#usercp_nav .blockrow .active{background-color: #d9d4dd!important;}
.threadlisthead { /*Thread list heading and spacing*/
border: 1px solid #3a2449!important;
background: #613D7A url(http://images.anime-sharing.info/images/buttons/newbtn_middle.png) repeat-x!important;}
/*.pagination span a{color:#261830;border: 1px solid #EE749B;}*/
.breadcrumb .crust a.crumb{color: #432a55!important;}
.breadcrumb .crust b.lastcrumb{color:#7F7F7F!important;}
/*footer bg*/
.footer {background: #613D7A url(http://pile.randimg.net/0/159/39883/otomeASSakimichi.png) center top no-repeat!important;padding: 5px 0px 5px 0px!important;border: 1px solid  #613D7A!important; box-shadow:none!important;}
.forumhead + .childforum .L2:nth-child(odd) .forumrow, .forumhead + .L2 .forumrow {background: #E4E4E4 none left bottom!important;}
.cms_widget .widget_post_useravatar img, .block .widget_post_useravatar img{
 min-width: 30px;
max-width: 30px !important;
height: 30px!important;
-webkit-border-radius: 50%;
-moz-border-radius: 50%;
border-radius: 50%;
-webkit-box-shadow: 0 0 0 3px #fff, 0 0 0 4px #999, 0 2px 5px 4px rgba(0,0,0,.2);
-moz-box-shadow: 0 0 0 3px #fff, 0 0 0 4px #999, 0 2px 5px 4px rgba(0,0,0,.2);
box-shadow: 0 0 0 3px #fff, 0 0 0 4px #999, 0 2px 5px 4px rgba(0,0,0,.2);}
li.activitybit a, li.activitybit a:hover{color:#432a55;}
/*Uncomment radius and margin bottom if you want to make it less girly*/
.forumbit_post .forumrow, .forumbit_nopost .forumhead, .forumbit_nopost .forumrow, .forumbit_post .forumhead, .forumbits .forumhead {
-moz-box-shadow: none;
-webkit-box-shadow: none;
box-shadow: none; 
/*border-radius: 5px;
margin-bottom: 2px;*/  }
#usercp_nav .blockbody {border: 1px solid #432a55;}
.blockbody.settings_form_border {
border-right: 1px solid #432a55;
border-left: 1px solid #432a55;
border-bottom: 1px solid #432a55;
}
#new_subscribed_forums .forumbit_nopost .forumbit_nopost .forumrow, #new_subscribed_forums .forumbit_post .forumrow{border:0}
li.activitybit img {
height: 70px;
width: 70px;
-webkit-border-radius: 50%;
-moz-border-radius: 50%;
border-radius: 50%;
-webkit-box-shadow: 0 0 0 3px #fff, 0 0 0 4px #999, 0 2px 5px 4px rgba(0,0,0,.2);
-moz-box-shadow: 0 0 0 3px #fff, 0 0 0 4px #999, 0 2px 5px 4px rgba(0,0,0,.2);
box-shadow: 0 0 0 3px #fff, 0 0 0 4px #999, 0 2px 5px 4px rgba(0,0,0,.2);
}
#forums .L1 .forumhead a{color:white!important}/*
.toplinks ul.isuser li.welcomelink{color:white!important}*/
.toplinks .notifications a.popupctrl,.toplinks ul.isuser li a{color:white!important}
.toplinks ul.isuser .notifications .popupbody li a{background: #613D7A!important;}
a.navtab {color: white!important;}
.navtabs > ul.floatcontainer li a {color: rgb(255,255,255)!important;}
#navtabs .popupmenu ul, #navtabs .popupmenu ul li a {height: auto;background: #333333!important;}
.forumbit_nopost .forumhead .forumtitle a{color:white!important;}
.textbox, textarea, select{border: 1px solid #613D7A!important;}
.tabs li{border-right: 1px solid #3a2449!important;}
.tabs{border-bottom: 1px solid #3a2449!important;}
.navbar_advanced_search li a,.threadlisthead a, .threadlisthead a:hover,.newcontent_textcontrol:hover,.newcontent_textcontrol{color:white!important;}
a.postcounter,h2.searchlisthead a{color: white!important;}
.bbcode_container div.bbcode_quote{border: 1px solid #613D7A!important;}
.toplinks ul.isuser .notifications .popupbody li a:hover{background:#333333!important;}
.navtabs > ul.floatcontainer .popupbody li a:hover{background:#432a55!important;}
.footer a, .footer a:hover{color:white!important;}
.formcontrols {
border-right: 1px solid #613D7A!important;
border-left: 1px solid #613D7A!important;
  border-bottom: 1px solid #613D7A!important;}
.navtabs li a.navtab{color:white!important; border:0;}
li.floatcontainer:nth-child(odd) {background: whitesmoke;}
.postbitlegacy .userinfo{background: whitesmoke;}
.logo-image {
clear: both!important;
display: block!important;
float: none!important;
text-align: center!important;
overflow: hidden!important;
background: url(http://pile.randimg.net/0/159/39883/otomeASSakimichi.png) center top no-repeat!important;/*header*/
-webkit-background-size: cover;
-moz-background-size: cover;
-o-background-size: cover;
background-size: cover;  
}
.postbit .posthead .nodecontrols a, .postbitlegacy .posthead .nodecontrols a, .eventbit .eventhead .nodecontrols a{color:white!important;}
.postbitdeleted .posthead, .postbitignored .posthead{
background: #613D7A url(http://images.anime-sharing.info/images/buttons/newbtn_middle.png) repeat-x!important;
border: 1px solid #613D7A!important;}
.notices li{border: 1px solid #9DCC53!important;}
/*icon legend PM*/
#forum_icon_legend h5 { 
  border: 1px solid #613D7A!important;
  background: #613D7A  url(http://images.anime-sharing.info/images/buttons/newbtn_middle.png) repeat-x!important;
}
/*pm border*/
.pmbit, .visitormessage {
  border-right: 1px solid #613D7A!important;
  border-left: 1px solid #613D7A!important;}
.pmlist .blockrow {border-top: solid 1px #d9d4dd!important;}
/*front border below subforums*/
.forumbit_post .forumrow, .forumbit_nopost .forumhead, .forumbit_nopost .forumrow, .forumbit_post .forumhead, .forumbits .forumhead { 
/*box-shadow:none!important;
border-top: #3a2449 1px solid!important;*/}
/*front forum radius*/
.forumhead.foruminfo.L1.collapse {
  border-top-right-radius: 5px!important;
  border-top-left-radius: 5px!important;
}
.forumhead.foruminfo.collapse {
  border-top-left-radius: 5px!important;
  border-top-right-radius: 5px!important;
}
.forumbit_nopost .forumhead .forumtitle a{text-transform: uppercase!important;}
/*navigation stripe*/
.navtabs ul {background: url(http://i.want.tf/to/that/ksxRbo5w.png) 100% 100% repeat-x!important;}  
/*pagination hover*/
.pagination span a:hover {background-color: #261830!important;}
/*small pop-up*/
.popupgroup .popupmenu a.popupctrl{color:white!important;}
/*tolls above thred bg*/
.toolsmenu{background: url(http://i.want.tf/to/that/ksxRbo5w.png) 100% 100% repeat-x!important;}
/*index button*/
.blockfoot, .blocksubfoot {background: #e9e9e9 none repeat-x!important;}
/*hover menu stuff in userCP*/
.popupbody li a:hover,#usercp_nav li a:hover{background:#d9d4dd!important;}
/*latest topics top bg*/
.mainbox{background:none!important;}
.right-mainbox{background: url(http://i.want.tf/to/that/ahoHq.png) no-repeat top right!important;}
/*search*/
#searchtypeswitcher li a{background: #613D7A url(http://i.imgur.com/AkR4ftu.png) 100% 100% repeat-x!important;
color:#ffffff!important;}
#searchtypeswitcher li a:hover{background: #432a55 url(http://i.imgur.com/AkR4ftu.png) 100% 100% repeat-x!important;
color:#E5E5E5!important;}
#searchtypeswitcher { border-bottom: 4px solid #432a55;}

}
