css:
@-moz-document url-prefix("http://anime-sharing.com/"), url-prefix("http://www.anime-sharing.com/forum/") {
/*lightest c49699, main color 613D7A , dark color 371214, Link 954247, nav 301E3D, border 6e2428*/
h2.searchlisthead{background: #613D7A url(http://images.anime-sharing.info/images/buttons/newbtn_middle.png) repeat-x!important;
  border: 1px solid #954247!important;}
/*thread title front page*/
.forumhead {
background: #8A2D33 url(http://i.imgur.com/AkR4ftu.png) repeat-x!important; 
border: 1px solid #954247!important;
}
body a {color: #954247!important;}
#sidebar_container a:hover {color: #371214!important;}
#sidebar_container a {color: #954247!important;}
div#google_ads_iframe{dispaly:none!important}
.smallavatartext.widget_post_comment:nth-child(odd) {
background-color: floralwhite!important;
}
/*li.avatarcontent.floatcontainer.widget_post_bit {}
.smallavatartext.widget_post_comment{}*/
li.avatarcontent.floatcontainer.widget_post_bit:nth-child(odd){background-color: floralwhite!important;}

.postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead {
background: #8A2D33 url(http://images.anime-sharing.info/images/buttons/newbtn_middle.png) repeat-x!important;
border: 1px solid #8A2D33!important;
}

.navbar {background: #8A2D33 none!important;}
.newcontent_textcontrol:hover {
background: #371214 url(http://images.anime-sharing.info/images/buttons/newbtn_middle.png) repeat-x left top!important;}

body a:hover, body a:hover .time, body a:hover .shade, body a:hover .understate {
color: #8A2D33!important;}

.newcontent_textcontrol {
background: #8A2D33 url(http://images.anime-sharing.info/images/buttons/newbtn_middle.png) repeat-x left top!important;}
.blockhead {background: #8A2D33 url(http://images.anime-sharing.info/images/buttons/newbtn_middle.png) repeat-x!important;border: 1px solid #8A2D33!important;}
.breadcrumb .crust:hover a.crumb {color: #8A2D33;}
.pagination span.selected a {background: #8A2D33 url(http://images.anime-sharing.info/images/buttons/newbtn_middle.png) repeat-x!important; border: 1px solid #8A2D33!important;}
/* pagination page*/
.pagination span a.popupctrl,.pagination span a:hover, .pagination span a{background-color:#8A2D33!important;border: 1px solid #8A2D33!important;}
.pagination span a:hover {border: 1px solid #8A2D33!important;}
.pagination span a:hover,.pagination span a {color: white!important;}
.navtabs li.selected a.navtab{background: #8A2D33 repeat-x!important;color:white!important;}
.navtabs li a.navtab:hover {background: #371214!important; border-right: thick double #6e2428!important;} /*nav hover*/
.navtabs > ul.floatcontainer li a:hover {background: #333333!important;text-decoration: none;}
#forums a.username{color:#8A2D33;}
#forums a.username:hover{color:#371214;}
#usercp_nav .blockrow .active{background-color: #c49699!important;}
.threadlisthead {
border: 1px solid #8A2D33!important;
background: #8A2D33 url(http://images.anime-sharing.info/images/buttons/newbtn_middle.png) repeat-x!important;}
/*.pagination span a{color:#371214;border: 1px solid #EE749B;}*/
.breadcrumb .crust a.crumb{color: #954247!important;}
.breadcrumb .crust b.lastcrumb{color:#7F7F7F!important;}
.footer {background: #8A2D33 none!important;padding: 5px 0px 5px 0px!important;border: 1px solid  #8A2D33!important; box-shadow:none!important;}
.forumhead + .childforum .L2:nth-child(odd) .forumrow, .forumhead + .L2 .forumrow {background: #E4E4E4 none left bottom!important;}
.cms_widget .widget_post_useravatar img, .block .widget_post_useravatar img{
 min-width: 30px;
max-width: 30px !important;
height: 30px!important;
-webkit-border-radius: 50%;
-moz-border-radius: 50%;
border-radius: 50%;
-webkit-box-shadow: 0 0 0 3px #fff, 0 0 0 4px #999, 0 2px 5px 4px rgba(0,0,0,.2);
-moz-box-shadow: 0 0 0 3px #fff, 0 0 0 4px #999, 0 2px 5px 4px rgba(0,0,0,.2);
box-shadow: 0 0 0 3px #fff, 0 0 0 4px #999, 0 2px 5px 4px rgba(0,0,0,.2);}
li.activitybit a, li.activitybit a:hover{color:#954247;}
/*Uncomment radius and margin bottom if you want to make it less girly*/
.forumbit_post .forumrow, .forumbit_nopost .forumhead, .forumbit_nopost .forumrow, .forumbit_post .forumhead, .forumbits .forumhead {
-moz-box-shadow: none;
-webkit-box-shadow: none;
box-shadow: none; 
/*border-radius: 5px;
margin-bottom: 2px;*/  }
#usercp_nav .blockbody {border: 1px solid #954247;}
.blockbody.settings_form_border {
border-right: 1px solid #954247;
border-left: 1px solid #954247;
border-bottom: 1px solid #954247;
}
#new_subscribed_forums .forumbit_nopost .forumbit_nopost .forumrow, #new_subscribed_forums .forumbit_post .forumrow{border:0}
li.activitybit img {
max-height: 70px;
min-width: 70px;
-webkit-border-radius: 50%;
-moz-border-radius: 50%;
border-radius: 50%;
-webkit-box-shadow: 0 0 0 3px #fff, 0 0 0 4px #999, 0 2px 5px 4px rgba(0,0,0,.2);
-moz-box-shadow: 0 0 0 3px #fff, 0 0 0 4px #999, 0 2px 5px 4px rgba(0,0,0,.2);
box-shadow: 0 0 0 3px #fff, 0 0 0 4px #999, 0 2px 5px 4px rgba(0,0,0,.2);
}
#forums .L1 .forumhead a{color:white!important}/*
.toplinks ul.isuser li.welcomelink{color:white!important}*/
.toplinks .notifications a.popupctrl,.toplinks ul.isuser li a{color:white!important}
.toplinks ul.isuser .notifications .popupbody li a{background: #8A2D33!important;}
a.navtab {color: white!important;}
.navtabs > ul.floatcontainer li a {color: rgb(255,255,255)!important;}
#navtabs .popupmenu ul, #navtabs .popupmenu ul li a {height: auto;background: #333333!important;}
.forumbit_nopost .forumhead .forumtitle a{color:white!important;}
.textbox, textarea, select{border: 1px solid #8A2D33!important;}
.tabs li{border-right: 1px solid #6e2428!important;}
.tabs{border-bottom: 1px solid #6e2428!important;}
.navbar_advanced_search li a,.threadlisthead a, .threadlisthead a:hover,.newcontent_textcontrol:hover,.newcontent_textcontrol{color:white!important;}
a.postcounter,h2.searchlisthead a{color: white!important;}
.bbcode_container div.bbcode_quote{border: 1px solid #8A2D33!important;}
.toplinks ul.isuser .notifications .popupbody li a:hover{background:#333333!important;}
.navtabs > ul.floatcontainer .popupbody li a:hover{background:#954247!important;}
.footer a, .footer a:hover{color:white!important;}
.formcontrols {
border-right: 1px solid #8A2D33!important;
border-left: 1px solid #8A2D33!important;
  border-bottom: 1px solid #8A2D33!important;}
.navtabs li a.navtab{color:white!important; border:0;}
li.floatcontainer:nth-child(odd) {background: whitesmoke;}
.postbitlegacy .userinfo{background: whitesmoke;}
.logo-image {
clear: both!important;
display: block!important;
float: none!important;
text-align: center!important;
overflow: hidden!important;
background: url(http://pile.randimg.net/0/159/39893/ASAyakashi.png) center top no-repeat!important;/*header*/
-webkit-background-size: cover;
-moz-background-size: cover;
-o-background-size: cover;
background-size: cover;  
}
.postbit .posthead .nodecontrols a, .postbitlegacy .posthead .nodecontrols a, .eventbit .eventhead .nodecontrols a{color:white!important;}
.postbitdeleted .posthead, .postbitignored .posthead{
background: #8A2D33 url(http://images.anime-sharing.info/images/buttons/newbtn_middle.png) repeat-x!important;
border: 1px solid #8A2D33!important;}
.notices li{border: 1px solid #9DCC53!important;}
/*icon legend PM*/
#forum_icon_legend h5 { 
  border: 1px solid #8A2D33!important;
  background: #8A2D33  url(http://images.anime-sharing.info/images/buttons/newbtn_middle.png) repeat-x!important;
}
/*pm border*/
.pmbit, .visitormessage {
  border-right: 1px solid #8A2D33!important;
  border-left: 1px solid #8A2D33!important;}
.pmlist .blockrow {border-top: solid 1px #c49699!important;}
/*latest topics top bg*/
.mainbox{background:none!important;}
.right-mainbox{background: url(http://i.imgur.com/qnG5yoS.png)no-repeat top right!important;}
}
