css:
@-moz-document url("http://dj4br.home.t-link.de/lautspr.htm") {
html {
  font-size: 16px;
}

body {
  background-image: none;
  
  font-family: Georgia, serif;
}

p {
  max-width: 720px;
  margin: auto;  
  letter-spacing: .01rem;
  line-height: 1.5;
}

p + p {
  margin-top: 2em;
}


p > img {
  margin-top: 2em;
  margin-bottom: 2em;
  -webkit-filter: contrast(200%);
     -moz-filter: contrast(200%);
          filter: contrast(200%);
}

[align] {
  text-align: left;
}

p:nth-of-type(1),
p:nth-of-type(2),
p:nth-of-type(3),
p:nth-of-type(4),
p:nth-of-type(5),
p:nth-of-type(7),
p:nth-of-type(8),
p:nth-of-type(39),
p:nth-of-type(41),
p:nth-of-type(42),
p:nth-of-type(43),
p:nth-of-type(44),
p:nth-of-type(45),
p:nth-of-type(46) {
  display: none;
}

p:nth-of-type(6) {
  border-left: 2px dashed #999;
  padding-left: 2em;
}

p:nth-of-type(10) {
  margin-top: 0;
  font-weight: normal;
}

p:nth-of-type(6),
p:nth-of-type(10) {
  color: #999;
}

p > img,
p big b:only-child,
p:nth-of-type(9) big {
  display: block;
  width: 140%;
  margin-left: -20%;
}

p big b:only-child,
p:nth-of-type(9) big,
p:nth-of-type(10) {
  text-align: center;
  font-family: Helvetica, sans-serif; 
}

p:nth-of-type(9) big {
  font-size: 150%;
}

p big b:only-child {
  font-size: 150%;
}

p:nth-of-type(6),
p:nth-of-type(10) {
  font-size: 80%;
}
}
