css:
@-moz-document url-prefix("https://www.zhihu.com/")
{    
    body.zhi {
        font-weight:normal !important;
    }
    
    h2 {
        font-weight:normal;
    }
    
    /* 主页"最新动态"四个字 */
    
    div#zh-home-list-title.zg-section {
    }
    
    /* 问题 */
    
    a.question_link {
        font-weight:normal !important;
        color:#000
    } 
    
    /* 回答者名字 */
    
    h3.zm-item-answer-author-wrap {
        font-weight:normal !important;
    }
    
    a.name {
        font-weight:normal !important;
    }
    
    span.zm-profile-section-name {
        font-weight:normal !important;
    }
    
    /* 页面最下方写答案的名字 */
    
    div.zu-answer-form-title {
        font-weight:normal !important;
    }
    
    /* 回答者介绍 */
    
    strong.zu-question-my-bio {
        font-weight:normal !important;
    }
    
    div.author-info {
        font-weight:normal !important;
    }
    
    /* 答案 */
    
    div.zm-editable-content {
    }
    
    div.content {
    }
    
    /* 含有单个答案的页面的问题标题 */
    
    div.zh-question-title {
        font-weight:bold !important;
    }
    
    /* 收藏页中问题的标题 */
    
    h2.zm-item-title {
        font-weight:normal !important;
    }
    
    /* 主页中专栏文章的标题 */
    
    a.post-link {
        font-weight:normal !important;
    }
    
    /* 答案页中答案的标题 */
    
    h2.zm-item-title.zm-editable-content {
        font-weight:bold !important;
    }
    
    /* 答案内容中的粗体 */
    
    b {
        font-weight:normal !important;
        text-decoration:underline !important;
    }
    
    /* 其他的加粗文字 */
    
    strong {
        font-weight:normal !important;
    }
    
    /* 边栏标题 */
    
    h3 {
        font-weight:normal !important;
    }
    
    /* 个人页面 “提问” “回答” “专栏文章”... 几个字 */
    
    a.item {
        font-weight:normal !important;
    }
    
    /* 个人页面 专栏和标题旁边 “关注了” 三个字 */
    
    div.zm-profile-side-section-title {
        font-weight:normal !important;
    }
    
    /* 个人页面 “最新动态” 四个字 */

    h2.zm-profile-section-title {
        font-weight:bold !important;
    }
    
    /* 个人页面 赞同数 */

    div.zm-profile-vote-num {
        font-weight:normal !important;
    }
    
    /*搜索栏
    
    input#q.zu-top-search-input {
    }
    
    button#zu-top-add-question.zu-top-add-question {
    }
    
    */
    
    /* 关注和取消关注按钮
    
    button.follow-button.zg-unfollow.zg-btn-white {
    }
    
    button.follow-button.zg-follow.zg-btn-green {
    }
    
    button.zg-btn.zg-btn-unfollow.zm-rich-follow-btn {
    }
    
    button.zg-btn.zg-btn-follow.zm-rich-follow-btn {
    }
    
    */
    
}
