css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('http://(filelist\\.ro|flro\\.org)/(?!login.php).*') {
.mainheader{
  background:
  radial-gradient(black 15%, transparent 16%) 0 0,
  radial-gradient(black 15%, transparent 16%) 8px 8px,
  radial-gradient(rgba(255,255,255,.06) 15%, transparent 20%) 0 1px,
  radial-gradient(rgba(255,255,255,.06) 15%, transparent 20%) 8px 9px;
  background-color:rgb(20, 20, 20);
  background-size:16px 16px;
}
#copyrightInnerRight > b:nth-child(1) > font:nth-child(1){
  display: none;
}
.statusbar{
  position: fixed;
  top:0px;
  right:0px;
  margin-top:0px;
  border-radius: 0px;
  z-index: 1000;
  box-shadow: 0px 0px 4px rgb(42, 42, 42);
}
.subheader{
   width: 100%;
  margin:0px;
}
#navigation{
  width:100%;
  margin:0px;
  border-left:0px;
  border-right:0px;
  border-radius: 0px;
  top:97px;
}
#nav{
  position: absolute;
  top: -4px;
}
.logo{
  width: 218px;
  height:98px;
  background-size: auto 98px;
}
.subheader{
  height: 98px;
}
.mainheader{
  height: 144px;
}
a{
  outline: 0px;
  transition: background-color .2s, color .2s;
}
a:focus{
  background-color:rgba(0,0,0, 0.1);
}
.gototop{
  position: fixed;
  right: 0px;
  bottom:0px;
  background-color: rgb(0, 0, 0);
  padding:5px;
  border-top-left-radius: 3px;
  box-shadow: 0px 0px 4px rgb(0, 0, 0);
}
/* Profile */
.myprofile{
  border: 0px;
  box-shadow: 0px 0px 3px gray;
}
.cblock-innercontent > div:nth-child(2) > form:nth-child(1) > fieldset{
  padding-left: 0px !important;
  padding-right: 0px !important;
  border: 0px;
  box-shadow: 0px 0px 3px gray;
}
.cblock-innercontent > div:nth-child(2) > form:nth-child(1) > fieldset label{
  margin-left: 20px;
}
.cblock-innercontent > div > form > fieldset > legend{
  width: 100% !important;
  text-align: center;
  line-height: 25px;
  height: 25px;
  font-size: 15px;
  box-shadow: 0px 0px 3px gray;
}
  
/* Freeleech notice*/

center > [color="green"] {
  position: fixed;
  bottom: 0px;
  left: 0px;
  background: linear-gradient(to bottom, rgb(40, 40, 40), rgb(0,0,0));
  vertical-align: center;
  padding: 15px 5px 15px 5px;
  z-index: 100;
  text-align: center;
  border-radius: 0px 4px 0px 0px;
  box-shadow: 0px 0px 4px rgb(0, 0, 0);
  color: rgb(200, 255, 200);
}
center > [color="green"] {
  color: rgb(50, 255, 50);
}
}
