css:
@-moz-document domain("devzing.com") {
html {
margin: 0 30px !important;
}

pre {
overflow: auto !important;
}

.bz_comment {
width: 100% !important;
margin: auto !important;
}

#comments {
/*margin: 0 50px !important;*/
}

#attachment_table, form > table {
width: 100% !important;
margin: auto !important;
}

#flags, #flags select {
width: 100% !important;
}

pre {
font-size: 1.05em !important;
}

body {
  font-family: "Helvetica Neue",Helvetica,Verdana,Arial,sans-serif;
  font-size: 100%;
  background-color: #25282c !important;
  color: #333;
}

#bugzilla-body {
  font-size: .8rem;
  background-color: white;
}

#header a, #footer a {
  color: #333;
}

#header #titles {
  background-color: #0072bc;
  border-top: none;
  border-bottom: 1px solid #0f6297;
  padding: 0.3rem;
}

#title p {
  font-family: "Helvetica Neue",Helvetica,Verdana,Arial,sans-serif;
  font-weight: normal;
  white-space: nowrap;
  text-shadow: none;
  color: #feffff;
  font-size: 1.6rem;
  line-height: 2rem;
  padding: 0 0 0 0.2rem;
}

#header .links, #footer {
  background-color: #dcdee0;
  color: #333;
}

#header ul.links li a, #links-saved ul.links li a {
  -moz-background-clip: border;
  -webkit-background-clip: border;
  -moz-background-clip: border;
  -o-background-clip: border;
  -webkit-background-clip: border;
  background-clip: border;
  -moz-background-origin: padding;
  -o-background-origin: padding;
  -webkit-background-origin: padding;
  background-origin: padding;
  border-radius: 5px;
  padding: 0px 4px 0px 4px;
  margin: 0 4px 0 0;
  text-decoration: none;
  position: relative;
  color: #feffff;
}

#header #lang_links_container ul.links li {
  display: inline-block;
}

#header ul.links li a:hover, #links-saved ul.links li a:hover {
  background-color: #FF9900;
}

.nactiveM {
  color: #ffffff;
  background-color: #777777;
}

.activeM {
  color: #ffffff;
  background-color: #ff9900;
}

.nactiveM[href="#"] {
  display: none !important;
}

.lang_current {
  display: none;
}

}
