css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("heise.de") {
div#forum_wrap {
    line-height: 100%;
  }
  .metaline, .paginierung_thread, #forum_wrap .paginierung_thread {
    background-color: darkblue;
    color: white;
    padding: 0;
  }

  #thread_sortierung.tree_mode .thread_expand_all, #thread_sortierung.tree_mode .thread_collapse_all, .paginierung,  .paginierung a, .paginierung .page, .paginierung em, #forum_wrap .paginierung_thread span.older, #forum_wrap .paginierung_thread span.newer {
    color: white;
  }
  .metaline a, .paginierung .newer, .paginierung .older, .paginierung .newer:hover, .paginierung .older:hover, .paginierung span.newer:hover, .paginierung span.older:hover, .paginierung span.newer, .paginierung span.older, #forum_wrap .paginierung_thread span.older, #forum_wrap .paginierung_thread span.newer, .paginierung span.older, #forum_wrap .paginierung_thread a.older, #forum_wrap .paginierung_thread a.newer, #forum_wrap .paginierung_thread a.older:hover, #forum_wrap .paginierung_thread a.newer:hover, #forum_wrap .paginierung_thread span.older:hover, #forum_wrap .paginierung_thread span.newer:hover, #forum_wrap .paginierung_thread span.page, #forum_wrap .paginierung_thread a, #forum_wrap .paginierung_thread em, #forum_wrap .paginierung_thread em:hover, #forum_wrap .paginierung_thread .vor, #forum_wrap .paginierung_thread .zurueck {
    color: white !important;
    border: none !important;
  }
  .posting_navi ul li ul li.posting, .metaline a, .paginierung a.newer, .paginierung a.older, .paginierung span.newer, .paginierung span.older, #forum_wrap .paginierung_thread a, #forum_wrap .paginierung_thread .newer, #forum_wrap .paginierung_thread .older, .paginierung span.page, #forum_wrap .paginierung_thread .vor, #forum_wrap .paginierung_thread .zurueck {
    font-weight: bold;
  }
  .metaline a, .paginierung a.newer, .paginierung a.older, #forum_wrap .paginierung_thread a {
    text-decoration: underline !important;
  }
  .posting_navi ul li ul li, .metaline .buttonline li, .metaline a.thread_button {
    border: none !important;
  }
  .buttonline li a, .posting_navi ul li ul li.posting, .metaline a.thread_button, .posting_navi ul li ul li {
    color: white;
  }
  .arrow.left {
    border-right-color: white !important;
  }
  .arrow.right {
    border-left-color: white !important;
  }
  .arrow.inactive.left {
    border-right-color: grey !important;
  }
  .arrow.inactive.right {
    border-left-color: grey !important;
  }
  .buttonline li a:hover, .metaline a.thread_button {
    outline: none;
  }
  #forum_wrap #forum_show_content {
    border-top: none;
  }
  ol#tree_thread_list {
    padding: 0;
  }
  #reply_create_posting #posting_create input[type="text"], #reply_create_posting #posting_create input[type="text"]:focus, #reply_create_posting #posting_create textarea, #reply_create_posting #posting_create textarea:focus {
    border-color: inherit;
    box-shadow: none;
    background-color: white;
    color: black;
    padding: 2px;
    
  }
  .paginierung_thread {
        padding: 2px !important;
        color: white !important;
        margin: 0 20px !important; /* Balken unten: funktioniert nicht. warum? */
  }
  a.rate_post {
    font-weight: bold;
    height: 25px !important;
    width: 30px !important;
    line-height: 25px !important;
  }
  ol#tree_thread_list > li > span.posting_element_hover > a.posting_subject {
    font-weight: normal !important;
  }
  
  ol#tree_thread_list li.posting_element time {
    width: 15ex !important;
  }
  
  div.tree_mode_switch span.smallnote {
    display: none;
  }
  div.tree_mode_switch {
    margin: 1px !important;
  }
  
  a.thread_button, #thread_sortierung, nav.posting_navi ul li, ul.buttonline li {
    margin-top: 0px !important;
  }

  /* posting date is cut off */
  ol#tree_thread_list li.posting_element time {
    width: 9em !important;
  }

  /* smaller */
  /*.tree_mode_switch {
    display: none;
  }
  .posting_navi .arrow {
    top: 0px !important;
  }
  .posting_navi ul li ul li, .buttonline li a, .metaline a.thread_button {
    padding: 0 10px;
  }
  */

a.a-button.article-footer__comment-button {
    padding: initial;
    background: initial;
    color: #039;
    border: initial;
    font-weight: bold;
    line-height: initial;
}
#icon-comments-outline {
    display: none !important;
}

/* Kommentar-Form Buttons */
/*.button, .forum_form_button {
    -webkit-appearance: push-button !important;
    -moz-appearance: button !important;
    color: inherit;
    background: initial !important;
    border: initial;
    padding: initial !important;
}*/

/* Beitrag kommentieren "Button" */

.comment-button, .comment-button:link, .comment-button:visited {
    color: darkblue;
    background-color: inherit;
    border: none;
    padding: 0;
    font-weight: bold;
}
.comment-button:hover {
    text-decoration: underline;    
}
.comment-button__icon {
    display: none;
}
}
