  height: 35px !important;
    padding: 0px !important;
    color: white !important;
    text-shadow: 0px 1px #385283;
    padding-left: 10px !important;
    line-height: 35px !important;
    border-bottom: 1px solid #3F5CA7 !important;
    border-top: 1px solid #3F5CA7 !important;
}

div.forum_category {
    border-radius: 3px 3px 0px 0px;
}

tr:nth-of-type(1) > td.forum_category { border-radius: 3px 3px 0px 0px; }
tr > td.forum_category:nth-of-type(2) { border-radius: 0px 0px 0px 0px; }

