css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("webkolleg.de") {
  
  #container { margin: 0 auto !important; }

  html, body, .main_right_left_body_container table, .main_right_left_body_container, th, td, h1, h2, h3, h4, p { font-family: Arial !important;}
  html, body, .main_right_left_body_container table { font-size: 13px !important;  }
  h1:first-child {font-size: 15px !important; margin: 0px 0 5px 0 !important;}
  h1 {font-size: 15px !important; margin: 20px 0 5px 0 !important;}
  h2 {font-size: 14px !important;}
  h3, h4 {font-size: 13px !important;}
  
  .main_right_left_body_container ul, .main_right_left_body_container li { list-style-type: disc !important; margin: 0 0 0 10px !important; }
  
  .teaser_home p { font-size: 12px !important; }
  
  strong strong { font-weight: bold !important; font-size: 15px !important; }
  
  /* linkfarben */
  .main_right_left_body_container a, .main_right_left_body_container a:link { color: #006600 !important;}
  .main_right_left_body_container a:visited { color: #7fb27f !important; }
  .main_right_left_body_container a:hover, 
    .main_right_left_body_container a:active, 
    .main_right_left_body_container a:focus { color: #cc0001 !important; }
  
  /* suche */ 
  #searchform input, #searchform select { padding: 5px !important; font-size: 14px !important; font-family: Arial !important; }
  #searchform select option { padding: 0 0 0 5px !important;  }

  /* seminarlisten */
  .tx-seminars-pi1 .tx-seminars-pi1-listrow th, .lister-table .top-row td { font-size: 14px !important; }
  .tx-seminars-pi1 .tx-seminars-pi1-listrow tbody .tx-seminars-pi1-title, .lister-table td { font-size: 12px !important; }
  
  .lister-table .top-row td { padding: 5px 2px !important;}
  
  /*.seminar-detail-view tr { vertical-align: top !important;}*/
  
  /* detailansicht seminar */
  .seminar-detail-lightgrey { background-color: #f0f0f0 !important; padding: 2px 5px !important; font-size: 12px !important; font-family: Arial !important; border: 0 solid #ddd !important; }
  .seminar-detail-darkgrey { background-color: #f9f9f9 !important; padding: 5px 0 5px 8px !important; font-size: 12px !important; font-family: Arial !important; border: 0 solid #ddd !important;}
  
 
  #seminar-registration-button, #seminar-remember-button {margin-top: 10px !important; margin-bottom: 5px !important; display: inline-block !important;}
  
  #seminar-registration-button a, #seminar-remember-button a {
    background: linear-gradient(to top, #509520 99%,#8ed247 100%) !important;
    border: 0 solid #275109 !important;
    padding: 5px 6px !important;
  }
  #seminar-registration-button a:hover, #seminar-remember-button a:hover {
    background: linear-gradient(to top, #509520 0%,#8ed247 1%) !important;
    text-decoration: none;
  }    

  /*anbieterliste */
  
  .organizer-table-first-column { width: 15px !important; background: linear-gradient(to right, #509520 0%,#8ed247 100%) !important; }
  .organizer-table-second-column { background-color: #f0f0f0 !important; padding: 7px !important; font-size: 12px !important; font-family: Arial !important;}
 
  /* navi */
  ul.box-menu-left li.act ul li.act ul li a {font-size: 11px !important;}
  
  /* footer */
  .main_right_left_body { margin-bottom: 30px !important; }
  .main_right_left_footer { padding: 20px 10px !important; margin: 10px 0 !important; text-align: center !important; }
  
  .tx-t3mshoworganizer-pi2 td {font-size: 13px !important; font-family: Arial !important; }
  
}
