css:
@-moz-document domain("androidpolice.com") {
.band.content.main .container.feature { 
  display:none;
}
          
.band.content.main .container .secondary.column {
  display:none;
}

.band.footer {
display:none
}

.band.content.main .container .primary.column {
width: 1000px;
}

.single-post-widget {
display:none
}

.article-nav {
display:none;
}

/* Top header background*/
.band.header {
/* background: url(../images/square_bg.png); */
background: #B2B2B2
}

/* Main background colour */
.band.content.main {
background-color: #000009;
}

/* Background of the single post */
.post {
background: #000000;
border: 1px solid #333333;
box-shadow: 0 1px 0 0 #242424
}

/* Colour of article title*/
a {
color: #BBA7A7;
}

/* Colour of text */
body {
color: #B2B2B2;
}
    
/* Colour of Section Headers */
.section-header {
background: #242424;
}

/* Colour of Note */
.note-note {
background-color: #331F00;
}

  
/* Removed borders & background of Galleries */
.gallery-display {
background: none;
border: none;
}

/* Application Widget*/
.post .pb-app-box .pb-app-widget-discover .pb-app-widget-table {
background: #333333;
}
.pb-app-widget-discover .pb-wd-data-appears img {
display: none;
}
.post .pb-app-box .pb-app-widget-discover .pb-app-widget-table .pb-wd-icon {
background: #333333;
}

/* Press release*/
blockquote.pr {
background: none;
}
blockquote.pr section {
background: #242424;
}
blockquote.pr section p {
color: #B2B2B2;
}
blockquote.pr footer {
background: #242424
}
blockquote.pr .fade-text {
background: none
}

/*Page Navigator*/
.wp-pagenavi {
background: #242424;
border-radius: 3px;
margin-bottom: 10px;
border: 1px solid #333333;
box-shadow: 0 1px 0 0 #242424;
}
}
