css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("atelier801.com") {

	
	
	
	

body{
  background-image: url(http://i.imgur.com/XW7LkPS.jpg) !important; background-attachment: fixed 
}
	
.barre-navigation {
    display: inline-block;
    list-style: outside none none;
    margin: 5px 10px 0px 0px;
    background-color: #2B2B2B;
    padding: 5px 10px;
    border-radius: 9px;
    float: left;
}
	
.barre-navigation a {
    color: #8C8C8C;
    text-decoration: none;
}



	
	

.table-section {
background-color: #1B1B1B !important;
}
.ligne-section-actu {
background-color: #252525 !important;
} 
	

	
.corps{
	padding-top:10px;
	padding-bottom:10px;
	height:100%
}
	
	
.cadre{
	min-height:10px;
	padding:2px 5px;
	border-radius:9px;
	color:#D8D8D8;
	background-color:#2A2A2A
}
	
.cadre-sujet {
background-color: #121212 !important;
	text-decoration:none
}
.cadre-sujet-postit {
background-color: #2D2D2D !important;
} 
	
	
.lien-blanc{	
color: #DFDFDF; 
}
	
	
.lien-blanc:focus {	
	color: #CBCBCB
}
		
.cadre-sujet-date {
    font-size: 14px;
    color: #626262;
}
	
.cadre-sujet-date:hover{
		color: #fff; 
	 text-shadow: 1px 1px 1px #fff !important;
}
	
.cadre-auteur-message-date {
    font-size: 11px;
    color: #A4A4A4;
}

.cadre * {
    line-height: normal;
}

.cadre-sujet-titre-sombre {
    color: #CACACA;
    margin-left: 3px;
    margin-right: 3px;
}
	
.cadre-sujet-titre-sombre:hover{
    color: #fff;

}
	

.cadre-sujet-infos {
opacity: 1 !important;
} 
	
.cadre-sujet-infos.lien-blanc {
		color: #fff !important;
} 
	
.cadre-sujet-infos.lien-blanc:hover {
		color: #fff !important;
	 text-shadow: 1px 1px 1px #fff !important;
} 
	
	
	


.cadre-type-auteur-joueur:hover,.cadre-type-auteur-joueur:active,.cadre-type-auteur-joueur.active,.cadre-type-auteur-joueur.disabled,.cadre-type-auteur-joueur[disabled]{
	color:#fff
}
	
.cadre-type-auteur-joueur {
    color: #D8D8D8;
	font-weight: bold;
}
	




.cadre-type-auteur-mapcrew{
	color:#2f7fcc;
font-weight: bold;
}

.cadre-type-auteur-mapcrew:hover,.cadre-type-auteur-mapcrew:active,.cadre-type-auteur-mapcrew.active,.cadre-type-auteur-mapcrew.disabled,.cadre-type-auteur-mapcrew[disabled]{
	color:#33a7ff
}

.cadre-type-auteur-sentinelle{
	color:#2ecf73;
font-weight: bold;
}

.cadre-type-auteur-sentinelle:hover,.cadre-type-auteur-sentinelle:active,.cadre-type-auteur-sentinelle.active,.cadre-type-auteur-sentinelle.disabled,.cadre-type-auteur-sentinelle[disabled]{
	color:#36ff8f
}

.cadre-type-auteur-moderateur{
	color:#babd2f;
font-weight: bold;
}

.cadre-type-auteur-moderateur:hover,.cadre-type-auteur-moderateur:active,.cadre-type-auteur-moderateur.active,.cadre-type-auteur-moderateur.disabled,.cadre-type-auteur-moderateur[disabled]{
	color:#f1f436
}

.cadre-type-auteur-admin{
	color:#eb1d51;
font-weight: bold;
}

.cadre-type-auteur-admin:hover,.cadre-type-auteur-admin:active,.cadre-type-auteur-admin.active,.cadre-type-auteur-admin.disabled,.cadre-type-auteur-admin[disabled]{
	color:#ff5860
}

.cadre-type-auteur-ancien-admin{
	color:#eb1d51;
font-weight: bold;
}

.cadre-type-auteur-ancien-admin:hover,.cadre-type-auteur-ancien-admin:active,.cadre-type-auteur-ancien-admin.active,.cadre-type-auteur-ancien-admin.disabled,.cadre-type-auteur-ancien-admin[disabled]{
	color:#ff5860
}
		
	
	
.cadre-quote small{
	color:#636363;
	margin-bottom:2px
}

.cadre-quote{
	background-color:#141414;
	font-size:small;
	padding:5px;
	margin:1px
}
	


	

.nombre-messages-nouveau {
    color: #00D25B;
}
.nombre-messages {
    font-size: 22px;
}
		
.numero-message {
    color: #A9A9A9;
    text-decoration: none;
}
	
.numero-message:hover{
    color: #fff;
    text-decoration: none;
}
	
.nombre-messages-reponses {
    color: #C4C700;
	  text-decoration: none;

}
	
	.nombre-messages-lu {
    color: #C9C9C9;
}

	

	
	
}
