css:
@-moz-document domain("web.whatsapp.com") {
.app-wrapper{background-color: #000000;}
.app-wrapper::before{background-color: #1D1D1D;}
.pane-list-body{background-color: #2D2D2D;}
.pane-list-header{background-color: #8d8d8d;}
.subheader-search{background-color: #8D8D8D;}
.active{background-color: #8D8D8D;}
.chat{background-color: #5D5D5D;}
.chat::before{background-color: #5D5D5D;}
.pane-chat-header{ background-color: #8d8d8d;}
.pane-chat-body{ background-color: #454545;}
.block-compose{ background-color: #939393;}
.drawer{background-color: #909090;}
.drawer-header{background-color: #D0D0D0;}
.pane-header{background-color: #D0D0D0;}
.imput{color: #000000;}
.list-title{ color: #000000;}
.active{border-bottom-color: #8d8d8d; background-color: #8c8c8c;}
.chat::hover{background-color: #8e8e94;}
.input-search::after{background-color: #8c8c8c;}
.pane-intro::after{border-top: #8c8c8c;}
.message-out{background-color: #DFDFDF;}
.backdrop{background-color: rgba(36, 35, 35, 0.85);}
.popup{background-color: rgba(36, 36, 36, 0.9);}
.popup-title{color: #FFFFFF;}
.btn-default{background-color: #b0b0b0;}
.btn-default:hover{background-color: #3d3d3d;}
.checkbox.checked{border-bottom: solid 2px #FDFDFD; border-right: solid 2px #FFFFFF;}
.checkbox.unchecked{border: 2px solid #FFFFFF;}
.select{color: #FFFFFF;}
.btn-plain{color: #FFFFFF;}
.btn-plain::hover{color: #FFFFFF;}
.dropdown{background-color :#eaeaea;}
}
