css:
@-moz-document url-prefix("https://www.tumblr.com"), url-prefix("http://www.tumblr.com") {
.post-typography-update .post .post-content, .post-typography-update .post .post_content {font-size: 14px;line-height: 1.3;}
.post-typography-update .post .post-content b, .post-typography-update .post .post-content strong, .post-typography-update .post .post_content b, .post-typography-update .post .post_content strong {font-weight: bold;}
.post-typography-update .post .post-content a, .post-typography-update .post .post_content a {text-decoration: underline;background-image: none;background-size:0px;}

.post_header {margin-bottom: 10px!important;}
.post_full .post_header {font-size: 13px;line-height:18px;height:18px;vertical-align: middle;}
.post.post_full .post_title {margin:0px;}
.link_button.clickable{margin-bottom:5px;}
.audio_player_container.has_art {margin-bottom:5px;}
.post_content.clearfix{margin-bottom:0px;}
.post_full .post_body {padding-top: 0px;}
.post_full .post_media {margin: 13px 0 5px 0;}    
.post-typography-update .post .post_content .editor>*:first-child, .post-typography-update .post .post_content .note_item
.text>*:first-child, .post-typography-update .post .post_content .answer.post_info>*:first-child, .post-typography-update .post .post-content .editor>*:first-child, .post-typography-update .post .post-content .note_item .text>*:first-child, .post-typography-update .post .post-content .answer.post_info>*:first-child {margin-top: 0px;margin-bottom:5px;}
.post-typography-update .post .post_content .post_body>*:first-child, .post-typography-update .post .post-content .post_body>*:first-child {margin-top: 0px;margin-bottom:5px;}
.post-typography-update .post .post-content p, .post-typography-update .post .post_content p {margin:0 0 5px 0;}
.post-typography-update .post .post-content blockquote, .post-typography-update .post .post_content blockquote {border-left: solid 4px rgba(0,0,0,.12);padding: 0 0 0 15px;margin: 0 0 8px 0;}
.post-typography-update .post .post_content pre, .post-typography-update .post .post_content pre {margin: 0 0 5px 0;}
.post-typography-update .post .post-content ul, .post-typography-update .post .post_content ul {padding-left: 20px;margin: 0px;margin-bottom: 5px;}
.post-typography-update .post .post-content ul li, .post-typography-update .post .post_content ul li {list-style-type: disc;padding-left: 0px;}
.photoset{padding:0px;margin-bottom:5px!important;}

.post_tags{margin-top:8px!important;margin-bottom:-5px;padding-bottom:0px;}
.post_footer.clearfix {margin-top: 0px;padding:0px;}

.post-typography-update .post.is_conversation .post_content .post_title, .post-typography-update .post.is_regular .post_content .post_title, .post-typography-update .post .post-content .title-field * {font-size: 20px;font-weight: bold;line-height: 1.3;font-family: "Helvetica Neue","HelveticaNeue",Helvetica,Arial,sans-serif;margin:0px;}

.post-typography-update .post .post-content h1, .post-typography-update .post .post-content h2, .post-typography-update .post .post_content h1, .post-typography-update .post .post_content h2 {font-family: "Helvetica Neue","HelveticaNeue",Helvetica,Arial,sans-serif;font-size: 15px;font-weight: bold;line-height: 1.3;margin-bottom: 5px;margin-top: 0px;}
.post-typography-update .post .post-content h3, .post-typography-update .post .post-content h4, .post-typography-update .post .post-content h5, .post-typography-update .post .post-content h6, .post-typography-update .post .post_content h3, .post-typography-update .post .post_content h4, .post-typography-update .post .post_content h5, .post-typography-update .post .post_content h6 {font-family: "Helvetica Neue","HelveticaNeue",Helvetica,Arial,sans-serif;font-weight: bold;font-size: 14px;line-height: 1.3;margin: 0px;color: #444;}
.is_quote .post_title, .post_full.is_quote .post_title.medium, .post_full.is_quote .post_title.medium big, .post_full.is_quote .post_title.medium small, .post-composer .medium .plaintext-editor {display:block!important;line-height: 1.3!important;font-size:16px!important;font-weight:bold!important;font-family: "Helvetica Neue","HelveticaNeue",Helvetica,Arial,sans-serif!important;margin-bottom: 0px!important;margin-top: 0px!important;}

.post-typography-update .post .post-content .note_wrapper .note_item, .post-typography-update .post .post_content .note_wrapper .note_item {padding: 10px;}
.post-composer_note-post .note_wrapper, .post_full.is_note .post-body .note_wrapper, .post_full.is_note .post_body .note_wrapper {margin-bottom: 10px;}
.post-typography-update .post .post_content .note_wrapper .note_item .asker {margin-bottom: 5px;}
.post-typography-update .post .post_content .answer.post_info {margin: 0px;padding: 0px;}
.post-typography-update .post .post_content .note_wrapper .note_item .answerer {margin-bottom: 5px;}

.media-container.photo-media-container,.audio-media-container.media-container.direct {margin-right: -10px;margin-bottom: 10px;}
.post-typography-update .post .post_content .post_body:last-child, .post-typography-update .post .post-content .post_body:last-child {margin-bottom: 0px;}

.source-field .editor-richtext {min-height:10px!important;}
.caption-field .editor-richtext {min-height:10px!important;}
.title-field{margin:0px;}
.post-typography-update .post .post-content .conversation_lines, .post-typography-update .post .post_content .conversation_lines{padding:0px;}
.chat-field .editor-plaintext, .chat-field .editor-placeholder {font-family: Courier,monospace;line-height: 21px;min-height: 5px;cursor: text;height: auto;padding:0px;}
}
