@-moz-document domain("facebook.com")
{ 
body
  {   
      background: url(http://i.imgur.com/4hzqsHq.jpg) no-repeat center fixed; 
      -webkit-background-size: cover;
      -moz-background-size: cover;
      -o-background-size: cover;
      background-size: cover;  
      font-size: 13px !important;
      font-family: "Courier New", Courier, monospace !important;  
    }


body>#blueBar
  { 
        background: none !important;
    }

.jewel, .jewelBox
  { 
        border: none !important; 
    }

.jewelBox
  { 
        background-color: rgba(255,255,255,0.9) !important;
    }

#headNavOut
  { 
        background: none !important; border: none !important;
    }

#pageNav a 
  { 
        color: #111 !important;
    }

#pageNav a:hover 
  { 
        color: #333 !important; background: none !important;
    }

#navAccount
  { 
        border: none !important;
    }

#navAccount ul
  { 
        background-color: rgba(255,255,255,0.9) !important;  border: none !important;
    }

#contentCol,#left_column,#right_column
  { 
        background-color: rgba(255,255,255,0.3) !important;  border: none !important;
    }

.inputtext,.add_comment_text,.uiMorePager, .box_header 
  {
        background-color: rgba(255,255,255,0.1) !important;
    }

.ufi_section, .photo_table, .photo_container
  {
        background: none !important; border: none !important;
    }

#sidebar_ads, #pagelet_adbox, #ego{display: none !important;}

/*Posts*/
.fcg 
  {    
      font-style: italic !important;
      font-size: 12px !important;
  }

.fwb 
  {
        font-weight: bold !important;
    }

._5pbx, ._5p3y ._5pbx span.text_exposed_link 
  {
    font-size: 14px !important;
      text-align: justify;
  }

.fbxPhoto{
   background-color: #fff !important;
  }

/*Top Bar*/

#blueBarHolder #blueBar, #blueBarNAXAnchor 
  {
        background: #363e6d !important;
    }

div#blueBarNAXAnchor._4f7n._xxp.fixed_elem::after 
  {
        background: #c96299 !important;
    }

div#userNavigationLabel, div#privacyFlyoutLabel, div._5ahz 
  {
        -webkit-filter: invert(50%);
    }

#fbRequestsJewel .jewelButton
  {
        background:url("http://i.imgur.com/1ObknyE.png") no-repeat center center!important
    }

#fbMessagesJewel .jewelButton, ._1z4y a.jewelButton
  {
        background:url("http://i.imgur.com/LPRi2uX.png") no-repeat center center!important
    }
#fbNotificationsJewel .jewelButton
  {
        background:url("http://i.imgur.com/ZX0W3Ap.png") no-repeat center center!important
    }

  
/* Transparencies */

div#u_0_1n 
  {
        color : rgb(0,0,255); background: rgba(255,255,255, 0.5);
    }

div._5rzs, div#leftCol  
  {
        background : rgba(255,255,255, 0.8);
    }

div._5qqe, div#pagelet_canvas_nav_content 
  {
        background : #333 ; opacity : 0.3;
    }  

div._5rzs, div._4-u2.mbm._5v6e, div#u_0_0 
  {
      background : rgba(255,255,255, 0.5);
    }

div#rightCol  
  {
      background : rgba(255,255,255, 0.0);
    } 

    
/*Chat Mod */
.navLink 
  {
        color: #ffffff !important;
    }

._rw 
  { 
        -webkit-filter: grayscale(20%); border-radius: 5px !important;
    }

.bubbles div._kso 
  { 
        background: none; border: 1px solid #222; -webkit-border-radius: 2px !important;
    }

.bubbles ._50kd ._kso 
  {
        background: none; -webkit-border-radius: 2px !important;
    }

.fbNubFlyoutTitlebar 
  {
        background: #222 !important; border: 1px solid #222 !important; border-radius: 1px !important;
    }

._5p3y[dir="ltr"], ._5p3y[dir="ltr"] button, ._5p3y[dir="ltr"] input, ._5p3y[dir="ltr"] label, ._5p3y[dir="ltr"] select, ._5p3y[dir="ltr"] td, ._5p3y[dir="ltr"] textarea, ._5p3y[dir="ltr"] .uiMentionsInput .highlighter, ._5p3y[dir="ltr"] .uiButtonText, ._5p3y[dir="ltr"] .uiButton input, ._5p3y[dir="ltr"] .uiLinkButton input, ._5p3y[dir="ltr"] button.as_link, ._5p3y[dir="ltr"] .fbChatSidebar, ._5p3y[dir="ltr"] .fbDock, ._5p3y[dir="ltr"] .fbFeedTicker .fbFeedTickerStory .tickerFeedMessage, ._5p3y[dir="ltr"] .fbFeedTicker .fbFeedTickerStory div.tickerFeedMessage h5, ._5p3y[dir="ltr"] .fbFeedTicker .fbFeedTickerStory div.tickerFeedMessage h6, ._5p3y[dir="ltr"] .tickerDialogContent .uiStream .uiStreamMessage, ._5p3y[dir="ltr"] .tickerDialogContent .uiStream .text_exposed_link, ._5p3y[dir="ltr"] .uiTooltipX .tooltipContent, ._5p3y[dir="ltr"] a[role="button"], ._5p3y[dir="rtl"] *[dir="ltr"] 
  {
      font-family: "Courier New", Courier, monospace !important;
    font-size: 12px !important;
  } 



/*

