body.fbIndex { 
 background: url(http://i.imgur.com/4hzqsHq.jpg) no-repeat center fixed; 
      -webkit-background-size: cover;
      -moz-background-size: cover;
      -o-background-size: cover;
      background-size: cover;  
      font-size: 11px !important;
      font-family: "Courier New", Courier, monospace !important;  
}

.loggedout_menubar_container { background-color: transparent !important; }
.fbIndex #globalContainer #dropmenu_container,
.fbIndex #globalContainer #content,
.fbIndex #globalContainer #pageFooter { display: none !important }


.fbIndex .loggedout_menubar_container {
  
  position: fixed !important;
  width: 475px !important;
  height: 82px !important;
  min-width: 0 !important;
  top: 50% !important;
  left: 50% !important;
  margin-top: -17px !important;
  margin-left: -210px !important;
  z-index: -1 !important;
}


.fbIndex .loggedout_menubar { width: auto !important; background-color: #3d4576 !important; padding-left: 10px; padding-bottom:20px ;border: 2px solid #fff;
    border-radius: 20px;}
.fbIndex .loggedout_menubar_container .lfloat,
.fbIndex .loggedout_menubar_container .rfloat { float: none !important }
