@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("unvirtual.medellin.unal.edu.co") {
  body{
   font-family: sans-serif !important;
   background: rgb(48,48,48)  url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAIAAAAmkwkpAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAClJREFUeNpiFBQU5OXlZQADJmFhYQYYYPr16xeQ+vz5M4gDEYPIAwQYAJUPBptp0pKhAAAAAElFTkSuQmCC") repeat !important;
   color: rgb(166,166,166) !important;
}
  .sitetopic .section .activity:hover, .course-content .section .activity:hover{
    background-color: rgba(63, 63, 63, 0.58);
    
  }
  h1, h2, h3, h4, h5, h6, p{
    text-shadow: 0px 1px 0px #000;
  }
  h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6{
    font-family: Myriad pro,"Ancizar serif","MS Serif","New York",serif;
  }
  .block .header h2{
    font-weight: bold;
  }
  #page-header .headermain{
    color:#989898;
    text-shadow:none; 
  }
  #site-news-forum h2, #frontpage-course-list h2, #frontpage-category-names h2, #frontpage-category-combo h2{
    border-bottom:1px solid #00A9F8;
    font-size:1.8em;
    color:#51BCFF;
  }
  #page-header,.block .header h2{
    box-shadow:  0px 1px rgba(255, 255, 255, 0.1) inset, 0px 1px 3px rgba(0, 0, 0, 0.5) !important;
  background: linear-gradient(rgb(50,50,50), rgb(40,40,40)) !important;
  color: rgb(166,166,166) !important;
  text-shadow: none !important;
  }
  .navbar{
    background-color:rgba(47, 47, 47, 0.58);
    border:1px solid #151515;
  }
  .sep{
    background: url("http://i59.tinypic.com/2v3h4wg.jpg") no-repeat scroll 100% 50% transparent;
  }
  .block_simple_clock .clock{
    font-size:1.5em;
    color:#289ED5;
    text-shadow: 0 0 1px;
  }
  .calendar_event_course{
    background-color:rgba(35, 143, 234, 0.38);
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.8);
  }
  #region-post{
    background:rgba(45, 45, 45, 0.57);
  }
  
  .select {
    padding:3px;
    margin: 0;
    -webkit-border-radius:4px;
    border-radius:4px;
    -webkit-box-shadow: 0 3px 0 #ccc, 0 -1px #fff inset;
    box-shadow: 0 3px 0 #ccc, 0 -1px #fff inset;
    background: #444;
    color:#888;
    border:none;
    outline:none;
    display: inline-block;
    -webkit-appearance:none;
    -moz-appearance:none;
    cursor:pointer;
}
  #yui_3_15_0_3_1423185997585_382{
    position:relative;
  }
  #yui_3_15_0_3_1423185997585_382:after{
    content:'<>';
    font:11px "Consolas", monospace;
    color:#aaa;
    -webkit-transform:rotate(90deg);
    -moz-transform:rotate(90deg);
    -ms-transform:rotate(90deg);
    transform:rotate(90deg);
    right:8px; top:2px;
    padding:0 0 2px;
    border-bottom:1px solid #ddd;
    position:absolute;
    pointer-events:none;
  }
  #yui_3_15_0_3_1423185997585_382:before{
    content:'';
    right:6px; top:0px;
    width:20px; height:20px;
    background:#f8f8f8;
    position:absolute;
    pointer-events:none;
    display:block;
  }
  .coursebox{
    border-color: #3C3C3C;
    border-width: 0px 0px 3px;
  }
  .no-overflow p span{
    color: #FFF !important;
  }
  #intro, #pageintro{
    background: none repeat scroll 0% 0% rgba(75, 75, 75, 0.39);
  }
  #intro{
    border-color:#121212;
  }
  table.flexible > tbody > tr:nth-of-type(2n+1), table.generaltable > tbody > tr:nth-of-type(2n+1){
    background-color:rgba(80, 80, 80, 0.52);
  }
  table.flexible > tbody > tr:nth-of-type(2n), table.generaltable > tbody > tr:nth-of-type(2n){
    background-color:rgba(36, 36, 36, 0.73);
  }
  .editcourse th, .editcourse td, .generaltable th, .generaltable td, #page-admin-course-manage .generalbox th, #page-admin-course-manage .generalbox td, #attempts th, #attempts td, .environmenttable th, .environmenttable td, .forumheaderlist td, .forumheaderlist th{
    border:1px solid #4D4D4D;
  }
  td.submissionstatussubmitted, div.submissionstatussubmitted, a.submissionstatussubmitted:link{
    color:white;
    background-color: #3C3C3C;
    box-shadow:inset 0px 0px 15px 3px rgba(50, 170, 50, 0.75);
  }
  .generalbox{
    border-color: #444;
  }
  button, input[type=submit] ,.homelink a{
  border: 0;
    cursor:pointer;
  background: #5786c1;
  color: white;
  padding: 8px 14px;
  font-weight: bold;
  font-size: 18px;
  text-decoration: none;
  display: inline-block;
  /* needed for anchors */
  position: relative;
  box-shadow: 1px 0px #3a587f, 0px 1px #4171ae, 2px 1px #3a587f, 1px 2px #4171ae, 3px 2px #3a587f, 2px 3px #4171ae, 4px 3px #3a587f, 3px 4px #4171ae, 5px 4px #3a587f, 4px 5px #4171ae, 6px 5px #3a587f, 5px 6px #4171ae;
}
  .homelink a:hover,button:hover, button:focus, button.hover, button.focus, input[type=submit]:hover, input[type=submit]:focus, input[type=submit].hover, input[type=submit].focus {
  transform: translate(2px, 2px);
  box-shadow: 1px 0px #3a587f, 0px 1px #4171ae, 2px 1px #3a587f, 1px 2px #4171ae, 3px 2px #3a587f, 2px 3px #4171ae, 4px 3px #3a587f, 3px 4px #4171ae;
}
.homelink a,active,button:active, button.active, input[type=submit]:active, input[type=submit].active {
  transform: translate(4px, 4px);
  box-shadow: 1px 0px #3a587f, 0px 1px #4171ae, 2px 1px #3a587f, 1px 2px #4171ae;
}
  .comment-area textarea{
    background-color:rgba(24, 24, 24, 0.42);
    border:none;
    color:#D8D8D8 !important;
    font-size:1.3em;
  }
  a:visited{
    color:#317AE7;
  }
  a:hover, a:active{
    color:#1D8AC3;
    text-shadow: 0 0 2px;
  }
  #page-admin-course-manage .generalbox th, .editcourse .header, .results .header, #attempts .header, .generaltable .header, .environmenttable th, .forumheaderlist th{
    background:none repeat scroll 0% 0% #5D5D5D;
  }
  table.quizreviewsummary th.cell{
    background:none repeat scroll 0% 0% #353535;
  }
  table.quizreviewsummary td.cell{
    background:none repeat scroll 0% 0% rgba(27, 27, 27, 0.49);
  }
  #region-pre{
    background:url("http://i59.tinypic.com/24zcdit.jpg") repeat-y scroll right center transparent
  }
  .que .info{
    background:none repeat scroll 0% 0% #515151;
  }
  .que .formulation{
    background:none repeat scroll 0% 0% #272727;
  }
  .que .outcome{
    background:none repeat scroll 0% 0% #363636;
  }
  #page-calendar-view .eventlist .event .topic{
    border-color:#000;
    background:none repeat scroll 0% 0% #2D2D2D;
  }
  #page-calendar-view .eventlist .event .description{
    border-color:black;
  }
  .descriptionbox, .path-user .description{
    border:1px solid rgb(56, 56, 56);
  }
  .calendar-event-panel .yui3-overlay-content{
    background-color:#2A2A2A;
  }
  .forumpost .topic{
    background:none repeat scroll 0% 0% rgba(51, 51, 51, 0.46);
  }
  .texrender{
    background-color:white;
  }
  .a{
    background:#1B1B1B !important;;
  }
  .a td ul{
    color:#D5D5D5 !important;;
  }
 }
