css:
@-moz-document domain("ubuntu.jump-up.info") {
/*======================================
  ubuntu.jump-up.info liquid compact 2015/02
  @-moz-document domain("ubuntu.jump-up.info")
  ======================================*/
/*--- unwanteds ---*/
 dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
 body, div, h1, h2, h3, h4, h5, h6
  {box-sizing: border-box; -moz-box-sizing: border-box;}
 body, div, p, span, h1, h2, h3, h4, h5, h6 {
  font-family: inherit !important;
  font-weight: normal !important;
  line-height: 1.4em !important;
  }
 #main, #content {width: auto !important;}
 #main {padding: 0px 3em;}

/*--- header ---*/
 #head-wrap {background-size: cover !important;}

/*--- footer ---*/

/*--- page control ---*/
 #fixedfoot {width: 100% !important;}
 #navbuttons {right: 3em !important;}
/*--- main contents ---*/
 #posts_content, #sidebardx {position: relative !important;}
 #posts_content {width: 100% !important; margin: 0 -200pt 0 0 !important;}
 #posts_content>* {width: auto !important; margin: 0 200pt 0 0 !important;}
 #sidebardx {width: 200pt !important;}
}
