css:
@-moz-document domain(tumblr.com) {
/* Body & Header Styles */
.identity {
  background:url('http://i.imgur.com/9lmZtgt.png') !important;
  background-attachment:fixed !important;
  background-size:100% !important;
}
.l-content {
  background-color:rgba(149,69,58,.6) !important;
  border:10px solid rgba(149,69,58,.01); 
}
#main_help .l-content {
  background-color:#fff !important;
  border:0;
}
.l-header-container {
  background-color:rgba(149,69,58,.3); 
}
.right_column, .left_column {
  background-color:transparent !important;
}

.identity .right_column:after {
  background:none !important;
}

.tab_notice {
  border:2px solid rgba(39,29,28,.3) !important; 
  background-color:rgba(149,69,58,.3) !important;
}

.tab_anchor:before {
  color:rgba(255,255,255,.7) !important;
}

.logo .logo_anchor {
  opacity:.7;
}

.post_full .post_permalink {
  border:2px solid rgba(149,69,58,.8);
  background:rgba(149,69,58,.8);
}

.post_avatar  {
  background:transparent;
}

.post_avatar .post_avatar_link {
 background:transparent 50% 50% no-repeat;
background-size:cover;
}

.settings_group_wrapper {
  background-color:#fff !important;
}

#dashboard_useraction_following .l-container .l-content, #dashboard_promoted_directory .l-container .l-content, #dashboard_lookup_lookup_contacts .l-container .l-content, .white_pane {
  background-color:#fff !important;
  border:0 !important;
}


/* Side Bar */
#right_column .hide_overflow, .hide_overflow a, .control-item span, .identity .controls_section li, .sub_control, .icon_left, .small_links a, .count, #xkit-people-notifier-new-btn  .members, .radar_tumblelog_label div, .follow_list_item_blog .tumblelog_title, .follow_list_item_blog .tumblelog_name, .tab_notice_value, .icon_plus, .sidebar_link {
  color:rgba(39,29,28,.75) !important; 
}

.result_sub_title.count {
color:#888 !important;
}

.result_title.hide_overflow {
line-height:normal !important;
}

.hide_overflow, #xkit-people-notifier-new-btn  .members, .count {
  line-height: 36px;
  font-weight:bold;
}

.follow_list_item_blog .hide_overflow, .open_blog.with_subtitle .hide_overflow, .blog_url .hide_overflow, .controls_item_settings_blog .hide_overflow, .controls_item_settings_page .hide_overflow {
  line-height:16px !important;
}

li.section_header, .identity .controls_section .popover_button_blogs {
  border-bottom:2px solid rgba(39,29,28,.2) !important;
}

.controls_section li {
  border-top:1px solid rgba(39,29,28,.2) !important;  
}

.identity .controls_section .popover_button_blogs+li, .identity .controls_section .popover_blogs+li, .identity .controls_section li.section_header+li {
  border-top:none !important;
}

li.no_push {
  cursor:hand;
}

li.controls_section_item:hover, li.no_push:hover, .identity .controls_section.follow_list.recommended_tumblelogs>li.item:hover, .controls_section_item.selected, .controls_item:hover {
  background-color:rgba(149,69,58,.3) !important;
}

#sidebar_footer_nav, .radar .radar_footer .radar_avatar:before, .identity .controls_section.user_list li .follow_list_item_blog.has_follow_button:before {
  display:none;
}

#open_blog_link {
  font-size:11px !important;
}

#popover_button_blogs {
  margin-top:10px;
}

.currently_selected_blog {
  font-size:16px;
}


.blog_url .hide_overflow {
  color:rgba(0,0,0,.7) !important;
}

/* X Kit Add-on Specifics */
#xpeoplenotifier .hide_overflow, #xstats_ul .hide_overflow, #xkit-people-notifier-new-btn .members div {
  padding-left:40px;
}

#post_limit_checker_view .hide_overflow {
  padding-left:0px;
}

#xkit-people-notifier-new-btn .members div:after {
  content:' âžœ';
}

#xkit_outbox_ul li:nth-child(2):hover, #xinbox_mass_edit_li:hover, #xinbox_search_li:hover {
  background-color:rgba(149,69,58,.3) !important;
}

.messages, .delete_all_messages {
  color:rgba(39,29,28,.75) !important; 
}
}
