css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://gmakers.ru/wiki") {
	body {
		margin: 0 auto;
		max-width: 1200px;
		font-family: sans-serif !important;
	}
	div#mw-head {	
		max-width: 1200px;
		margin: 0 auto;
		left: 0;
	}
	div.mw-body {	
		/*padding-left: 5% !important;*/
	}
	div#mw-panel {
		position: absolute;
		top: 160px;
		left: auto; 
		right: auto;
	}
	div#left-navigation {
		margin-left: 0px;
		max-width: 1200px;
	}
	div#mw-page-base {
		margin-left: 160px;
	}
	div#mw-content-text {
		line-height: 20pt;
	}
	p, ol, ul, dd {
		max-width: 32em;
		font-size: 14pt;
	}
	div.vectorTabs li a {
		height: 22px;
	}
}
