css:
/*TELEFUNKEN'S SQUARED MOON*/



/*URLBAR*/

#urlbar, #searchbar-textbox {

         border-radius: 0px 0px 0px 0px !important;
         border-color: #666666 !important;
  margin-top: -5px;
  margin-bottom: -3px;
 }
.urlbar-input-box {
  padding-left: 5px;
}

/*APPMENUBUTTON*/

#appmenu-button {
  border-radius: 0 0 0px 0px;
}

/*TABARROWSCROLL*/

.tabbrowser-arrowscrollbox > .scrollbutton-up,
.tabbrowser-arrowscrollbox > .scrollbutton-down {
border: 1px solid #666666;
background: linear-gradient(to top, rgba(10%,10%,10%,.4) 0px, transparent 0px), linear-gradient(transparent, hsla(0,0%,45%,.1) 1px, hsla(0,0%,32%,.2) 80%, hsla(0,0%,0%,.2)),
            linear-gradient(-moz-dialog, -moz-dialog);
margin-left: -1px;
margin-right: -1px;
position: absolute;
}

.tabbrowser-arrowscrollbox > .scrollbutton-up:not([disabled]),
.tabbrowser-arrowscrollbox > .scrollbutton-down:not([disabled]) {
  border: 1px solid #666666;
  border-image: none;
}

.tabbrowser-arrowscrollbox > .scrollbutton-up:not([disabled]):hover,
.tabbrowser-arrowscrollbox > .scrollbutton-down:not([disabled]):hover {
  background: linear-gradient(to top, rgba(20%,20%,20%,.4) 0px, transparent 0px), linear-gradient(hsla(0,0%,100%,.3) 1px, hsla(0,0%,75%,.2) 80%, hsla(0,0%,60%,.2)),
                    linear-gradient(-moz-dialog, -moz-dialog);
}

.tabbrowser-arrowscrollbox > .scrollbutton-up[disabled],
.tabbrowser-arrowscrollbox > .scrollbutton-down[disabled] {
 border: 1px solid #666666;
 opacity: 1;
 list-style-image: none;
 width: 22px;
}

/*UNIFIEDBACKFORWARD*/

window:not([chromehidden~=toolbar]) #navigator-toolbox[iconsize=large][mode=icons] > :-moz-any(#nav-bar[currentset*="unified-back-forward-button"],#nav-bar:not([currentset])) > #unified-back-forward-button > #back-button > .toolbarbutton-icon {
border-radius: 0 0 0 0;
  padding: 2px;
padding-right: 6px;
padding-left: 6px;
margin-right: 0px;
  border: 1px solid #666666;
background: none;
  box-shadow: none ;
transition: none;
}

window:not([chromehidden~=toolbar]) #navigator-toolbox[iconsize=large][mode=icons] > :-moz-any(#nav-bar[currentset*="unified-back-forward-button"],#nav-bar:not([currentset])) > #unified-back-forward-button > #back-button:not([disabled="true"]):not([open="true"]):not(:active):hover > .toolbarbutton-icon {
  background-image: linear-gradient(to top, rgba(10%,10%,10%,.4) 0px, transparent 0px), linear-gradient(hsla(0,0%,100%,.3) 1px, hsla(0,0%,75%,.2) 80%, hsla(0,0%,60%,.2)), linear-gradient(-moz-dialog, -moz-dialog);
box-shadow: none;
border: 1px solid #666666;
}

window:not([chromehidden~=toolbar]) #navigator-toolbox[iconsize=large][mode=icons] > :-moz-any(#nav-bar[currentset*="unified-back-forward-button"],#nav-bar:not([currentset])) > #unified-back-forward-button > #back-button {
  -moz-image-region: rect(0, 18px, 18px, 0);
  padding-top: 3px;
  padding-bottom: 3px;
  -moz-padding-start: 2px;
  -moz-padding-end: 0;
}

window:not([chromehidden~=toolbar]) #navigator-toolbox[iconsize=large][mode=icons] > :-moz-any(#nav-bar[currentset*="unified-back-forward-button"],#nav-bar:not([currentset])) > #unified-back-forward-button > #back-button:not([disabled="true"]):not([open="true"]):not(:active):hover > .toolbarbutton-icon {
  background-color: hsla(210,48%,96%,.75);
  box-shadow: none;

}

window:not([chromehidden~=toolbar]) #navigator-toolbox[iconsize=large][mode=icons] > :-moz-any(#nav-bar[currentset*="unified-back-forward-button"],#nav-bar:not([currentset])) > #unified-back-forward-button > #back-button[disabled] > .toolbarbutton-icon {
  box-shadow: none;
  transition: none;
}


window:not([chromehidden~=toolbar]) #navigator-toolbox[iconsize=large][mode=icons] > :-moz-any(#nav-bar[currentset*="unified-back-forward-button"],#nav-bar:not([currentset])) > #unified-back-forward-button > #back-button:not([disabled="true"]):hover:active > .toolbarbutton-icon,
window:not([chromehidden~=toolbar]) #navigator-toolbox[iconsize=large][mode=icons] > :-moz-any(#nav-bar[currentset*="unified-back-forward-button"],#nav-bar:not([currentset])) > #unified-back-forward-button > #back-button[open="true"] > .toolbarbutton-icon {
  background-image: linear-gradient(hsla(0,0%,100%,.6), hsla(0,0%,100%,.1));
  background-color: hsla(210,54%,20%,.15);
  border-color: #666666;
  box-shadow: 0 1px 1px hsla(210,54%,20%,.1) inset,
              0 0 1px hsla(210,54%,20%,.2) inset,
              /* allows windows-keyhole-forward-clip-path to be used for non-hover as well as hover: */
              0 1px 0 hsla(210,54%,20%,0),
              0 0 2px hsla(210,54%,20%,0);
  text-shadow: none;
  transition: none;
}

window:not([chromehidden~=toolbar]) #navigator-toolbox[iconsize=large][mode=icons] > :-moz-any(#nav-bar[currentset*="unified-back-forward-button"],#nav-bar:not([currentset])) > #unified-back-forward-button > #forward-button > .toolbarbutton-icon {
  padding: 2px;
padding-left: 6px;
padding-right: 6px;
transition: none;
  border: 1px solid #666666;
background: none;
  box-shadow: none ;
clip-path: none;
-moz-margin-start: -1px !important;
}



window:not([chromehidden~=toolbar]) #navigator-toolbox[iconsize=large][mode=icons] > :-moz-any(#nav-bar[currentset*="unified-back-forward-button"],#nav-bar:not([currentset])) > #unified-back-forward-button > #forward-button:not([open]):not(:active):not([disabled]):hover > .toolbarbutton-icon {
  background-image: linear-gradient(to top, rgba(10%,10%,10%,.4) 0px, transparent 0px), linear-gradient(hsla(0,0%,100%,.3) 1px, hsla(0,0%,75%,.2) 80%, hsla(0,0%,60%,.2)), linear-gradient(-moz-dialog, -moz-dialog);
  box-shadow: none;
border: 1px solid #666666;
}

window:not([chromehidden~=toolbar]) #navigator-toolbox[iconsize=large][mode=icons] > :-moz-any(#nav-bar[currentset*="unified-back-forward-button"],#nav-bar:not([currentset])) > #unified-back-forward-button > #forward-button {
  padding-top: 3px;
  padding-bottom: 3px;
  -moz-padding-start: 5px;
  -moz-padding-end: 2px;
}
window:not([chromehidden~=toolbar]) #navigator-toolbox[iconsize=large][mode=icons] > :-moz-any(#nav-bar[currentset*="unified-back-forward-button"],#nav-bar:not([currentset])) > #unified-back-forward-button > #forward-button:not([disabled="true"]):hover:active > .toolbarbutton-icon,
window:not([chromehidden~=toolbar]) #navigator-toolbox[iconsize=large][mode=icons] > :-moz-any(#nav-bar[currentset*="unified-back-forward-button"],#nav-bar:not([currentset])) > #unified-back-forward-button > #forward-button[open="true"] > .toolbarbutton-icon {
  background-image: linear-gradient(hsla(0,0%,100%,.6), hsla(0,0%,100%,.1));
  background-color: hsla(210,54%,20%,.15);
  border-color: #666666;
  box-shadow: 0 1px 1px hsla(210,54%,20%,.1) inset,
              0 0 1px hsla(210,54%,20%,.2) inset,
              /* allows windows-keyhole-forward-clip-path to be used for non-hover as well as hover: */
              0 1px 0 hsla(210,54%,20%,0),
              0 0 2px hsla(210,54%,20%,0);
  text-shadow: none;
  transition: none;
}


window:not([chromehidden~=toolbar]) #navigator-toolbox[iconsize=large][mode=icons] > :-moz-any(#nav-bar[currentset*="unified-back-forward-button"],#nav-bar:not([currentset])) > #unified-back-forward-button > .toolbarbutton-1:-moz-any([disabled],:not([open]):not([disabled]):not(:active)) > .toolbarbutton-icon {
  background: none;
  box-shadow: none;
  transition: none;
}

/*BUTTONS*/

#navigator-toolbox[iconsize=large][mode=icons] > #nav-bar .toolbarbutton-1 > .toolbarbutton-icon,
#navigator-toolbox[iconsize=large][mode=icons] > #nav-bar .toolbarbutton-1 > .toolbarbutton-badge-container,
#navigator-toolbox[iconsize=large][mode=icons] > #nav-bar .toolbarbutton-1 > .toolbarbutton-menubutton-button > .toolbarbutton-icon,
#navigator-toolbox[iconsize=large][mode=icons] > #nav-bar .toolbarbutton-1 > .toolbarbutton-menubutton-dropmarker > .dropmarker-icon {
  border-radius: 0 0 0 0;
  border-color: #666666;
  margin-top: -5px;
  margin-bottom: -3px;
  background: none    ;
}

#navigator-toolbox[iconsize=large][mode=icons] > #nav-bar .toolbarbutton-1:not([disabled]):-moz-any(:hover,[open]) > .toolbarbutton-menubutton-button > .toolbarbutton-icon,
#navigator-toolbox[iconsize=large][mode=icons] > #nav-bar .toolbarbutton-1:not([disabled]):hover > .toolbarbutton-menubutton-dropmarker > .dropmarker-icon,
#navigator-toolbox[iconsize=large][mode=icons] > #nav-bar .toolbarbutton-1:not([disabled]):not([checked]):not([open]):not(:active):hover > .toolbarbutton-icon,
#navigator-toolbox[iconsize=large][mode=icons] > #nav-bar .toolbarbutton-1:not([disabled]):not([checked]):not([open]):not(:active):hover > .toolbarbutton-badge-container {
  background-image: linear-gradient(to top, rgba(10%,10%,10%,.4) 0px, transparent 0px), linear-gradient(hsla(0,0%,100%,.3) 1px, hsla(0,0%,75%,.2) 80%, hsla(0,0%,60%,.2)), linear-gradient(-moz-dialog, -moz-dialog);
  border-color: #666666;
}


#navigator-toolbox[iconsize=large][mode=icons] > #nav-bar .toolbarbutton-1 > .toolbarbutton-menubutton-button:not([disabled]):hover:active > .toolbarbutton-icon,
#navigator-toolbox[iconsize=large][mode=icons] > #nav-bar .toolbarbutton-1[open] > .toolbarbutton-menubutton-dropmarker:not([disabled]) > .dropmarker-icon,
#navigator-toolbox[iconsize=large][mode=icons] > #nav-bar .toolbarbutton-1:not([disabled]):-moz-any([open],[checked],:hover:active) > .toolbarbutton-icon,
#navigator-toolbox[iconsize=large][mode=icons] > #nav-bar .toolbarbutton-1:not([disabled]):-moz-any([open],[checked],:hover:active) > .toolbarbutton-badge-container {
border-color: #666666;
}

#navigator-toolbox[iconsize=small][mode=icons] > #nav-bar .toolbarbutton-1 > .toolbarbutton-icon,
#navigator-toolbox[iconsize=small][mode=icons] > #nav-bar .toolbarbutton-1 > .toolbarbutton-badge-container,
#navigator-toolbox[iconsize=small][mode=icons] > #nav-bar .toolbarbutton-1 > .toolbarbutton-menubutton-button > .toolbarbutton-icon,
#navigator-toolbox[iconsize=small][mode=icons] > #nav-bar .toolbarbutton-1 > .toolbarbutton-menubutton-dropmarker > .dropmarker-icon {
  padding-top: 1px;
  }
  
/*CLOSEBUTTON*/

.tab-close-button {
margin-left: -3px;
}

/*TABSANDNEWTAB*/

.tabbrowser-tabs *|tab{       
    margin-left: -1px !important;
border: 1px solid #666666;
padding: 4px 2px 3px 3px;
background-size: calc(100% - 0px) calc(100% - 0px);
background-position: 1px 0px;
}

.tabs-newtab-button {       
    margin-left: -1px !important;
border: 1px solid #666666;
padding: 0px 0px 0px 0px;
background-size: calc(100% - 0px) calc(100% - 0px);
background-position: 1px 0px;
}

#new-tab-button {
 border-style: solid;
 border: 1px solid #666666;
position:absolute;
  background: linear-gradient(to top, rgba(10%,10%,10%,.4) 0px, transparent 0px), linear-gradient(transparent, hsla(0,0%,45%,.1) 1px, hsla(0,0%,32%,.2) 80%, hsla(0,0%,0%,.2)),
              linear-gradient(-moz-dialog, -moz-dialog);
margin-left: -1px;
margin-right: -1px;
}



#TabsToolbar > #new-tab-button:hover,
#TabsToolbar > toolbarpaletteitem > #new-tab-button:hover {
  background: linear-gradient(to top, rgba(20%,20%,20%,.4) 0px, transparent 0px), linear-gradient(hsla(0,0%,100%,.3) 1px, hsla(0,0%,75%,.2) 80%, hsla(0,0%,60%,.2)),
                    linear-gradient(-moz-dialog, -moz-dialog);
}

#TabsToolbar > #new-tab-button {
  width: 28px;
}

/*NOTIFICATIONPOPUP*/

#notification-popup-box {
  border-radius: 0px 0 0 0px;
}

/*TABSONTOPFIX*/

#nav-bar[tabsontop=true] {
  margin-top: 1px;
}
