css:
@-moz-document url-prefix("https://todoist.com/app") {
#all_holder {
  max-width: 1600px !important;
  margin: 0 80px !important;
  -webkit-box-shadow: 0 0 25px 2px #1C1C1C;
	box-shadow: 0 0 25px 2px #1C1C1C;
}
  
  /* =================================== */

body {background-image: url("http://s12.postimg.org/rjsif12pl/sunset_tartan.png"); }

div.invisible_bg {display: none;}

.go_premium {display: none;}


 #settings_popup {
  padding: 0; 
   background-color: #F9EFD6 !important;
 }
  
  #settings_content {
    padding: 10px;
	  background-color: #F9EFD6 !important;
    
}
  
  
  /* =================================== */


/* background colors navigation
*/
body {font-family: "Open Sans", sans-serif !important;} 

.has_team_inbox {background-color: #F4EAC7 !important;}
#td_logo, .invisible_bg {background-color: #F4EAC7 !important;}
.cmp_td_logo_mini {background-color: #F4EAC7 !important;}
.left_menu {background: #F4EAC7 !important;}

li.clickable.menu_clickable.indent_1.current {
background: #F3F3ED !important;}
li.clickable.menu_clickable.indent_2.current {
background: #F3F3ED !important;}
li.clickable.menu_clickable.indent_3.current {
background: #F3F3ED !important;}

/* improved task counter clarity
*/

.counter  {color: #1818F2 !important; opacity: 0.7 !important;font-weight: bold !important; font-size:95% !important;}
.counter_count {color: #1818F2 !important;opacity: 0.7 !important;font-weight: bold !important; font-size:95% !important;}

/*[[Brackets]]*/


/* better alignment of icons
*/


/*
.td_color div {border-radius: 12px; height: 10px;width: 10px;margin-top: -2px;margin-left: 6px;}
.td_color div img {display:none;}
*/

/* text colours, font size - nav menu
*/

.name {color: #000000 !important; font-size:1em !important;}
.filter {color: #000000 !important;font-size:1em !important;}
.control {color: #000000 !important;font-size:1em !important;}
.filter:hover, #left_menu .items li:hover {
background-color: #FFFFFF !important;}
.task_item:hover .content, .task_item:hover .assign_user, .task_item:hover .menu, .task_item:hover .project, .on_menu_show {
background-color: #F4EAC7 !important;}

.items .name{font-size:1em !important;}

/* text colours, font size, main text area text
*/

.text {color: #000000 !important;font-size:1em;}
.section_header a {color: #000000 !important;font-size:1em !important;}
.no_tasks a {color: #A1A1A1 !important;font-size:1em !important;}


/* main area background colors
*/

#content {background-color: #FFFFFF !important;}
div #top_bar {background-color: #FFFFFF !important;}


/* accent background colors (search box, tick box)
*/

input {background-color: #FFFFFF;}
.priority_1 .amicheckbox img {background-color: #FFFFFF;}
.task_item .task_content_item {
border-bottom: 1px solid #FFFFFF !important;
}


/* Custom logo, with size adjustment, and removal of border if large logo
*/
 
.cmp_td_logo_mini {
background-position: 0 -800px;
background: url("https://say-write.com/todoistlogo.png") no-repeat !important;
height: 27px !important;
width: 35px !important;
padding: 0 !important;
}
#td_logo {border-bottom: solid 1px #A1A1A1 !important; 
position: fixed !important; 
}

