@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("regnum.ru")
{
    
body {
    background-color: #CCCCCC;
}
	.article .content
	{
		margin-left: 0;
		overflow: hidden;
		width: 100%;
	}
	.main
	{
		float: left;
		width: 100%;
	}
	
	#weather,
	.currencybox,
	#socials-switcher,
	#panel-vkontakte,
	.socials-panel,
	#col3,
	#header-socials,
	#partincut,
	.twitter-share-button,
	#socialine,
	.btns,
	#yandexadvert,
	.pluginConnectButton,
	.fb-like,
	.fb_iframe_widget,
	.banner
	{
		display: none !important;
	}
	#colmid,
	#colmask,
	#col1wrap
	{
		width: 100%;
	
	}
	
#nav a {
    background-color: #fff;
    border-bottom: 1px solid #091c59;
    color: #091c59;
    display: block;
    padding: 0.4em 0;
    text-align: center;
    text-decoration: none;
    width: 270px;
}
	
#header {
    float: left;
    font-size: 85%;
    margin: 5px;
    padding: 0;
    width: 270px;
}
	
	.links_one
	{
		margin-right: 0px;
		width: 200px !important;
	}
	.links_two
	{
		margin-right: 0px;
		width: 200px !important;
	}
	.search
	{
		float: right !important;
		margin-right: 10px;
	}
	.storie_top table td
	{
		line-height: 1em;
		padding: 5px;
		vertical-align: top;
		width: 50%;
	}
    ul.headlineslist li {
        font-size: 0.9em !important;
        font-family: 'Roboto Condensed',Georgia,'Century Schoolbook L',serif !important;
			line-height: 0.9 !important;
    }
    .headlineslist {
        font-size: 0.9em !important;
        font-family: 'Roboto Condensed',Georgia,'Century Schoolbook L',serif !important;
    }
	
	.menu
	{
		background: -moz-linear-gradient(top, #1A618F, rgba(10, 35, 59, 99));
		background: -webkit-gradient(linear, left top, left bottom, from(#1A618F), to(rgba(10, 35, 59, 99)));
		background-image:linear-gradient( to bottom, rgb(26, 97, 143), rgb(10, 35, 59) );
		}
	.menu li a
	{
		color: white;
		font-weight: bold;
		line-height: 18px;
		padding: 4px 8px;
		text-decoration: none;
	}
	.menu li
	{
		height: 22px;
		line-height: 10px;
		padding: 4px 0px;
	}
	a#menu_more_button
	{
		color: white;
		font-weight: bold;
		text-decoration: none;
	}

	.menu_more ul
	{
		background-color: #444444;
		color: black;
		height: 22 !important;
		padding: 0;
	}
}
