css:
@-moz-document url-prefix('http://192.168.1.1'), regexp('https?://192.168[^/]\+1/.*') {
	* {
		box-sizing: border-box !important;
		font-family: "Helvetica Neue", "Helvetica", sans-serif !important;
	}

	body {
		min-width: 80em !important;
		background: white !important;
		color: rgb(64,64,64) !important;
	}

	h2 {
		border: none !important;
		background: transparent !important;
		color: inherit !important;
	}

	a {
		color: rgb(30,130,194) !important;
	}


	legend {
		background: transparent !important;
		color: rgb(143,191,59) !important;
	}

	fieldset {
		border: 1px solid rgba(64,64,64,0.2) !important;
		border-radius: 0 !important;
	}

	input.button {
		border: 1px solid rgba(64,64,64,0.2) !important;
		background: none !important;
		box-shadow: none !important;
		color: inherit !important;
	}

	input.button:hover {
		background-color: rgb(30,130,194) !important;
		color: white !important;
	}

	th {
		border: none !important;
		border-radius: 0 !important;
		background: rgba(64,64,64,0.5) !important;
		color: white !important;
	}

	#wrapper {
		width: 100% !important;
	}

	#content {
		width: 80em !important;
		padding-top: 15em !important;
		margin: auto !important;
	}

	#header {
		position: absolute !important;
		top: 0 !important;
		left: 0 !important;
		width: 100vw !important;
		min-width: 80em !important;
		height: 15em !important;
		padding: 0 !important;
		border-bottom: 4em solid rgb(30,130,194) !important;
		background: rgb(64,64,64) !important;
	}

	#header a, #statusInfo a {
		color: rgba(255,255,255,0.5) !important;
	}

	#logo, .logo {
		width: 70em !important;
		height: 5em !important;
		margin: auto !important;
		background: none !important;
		text-align: center !important;
	}

	#logo::after, .logo::after {
		content: "/*[[ssid]]*/" !important;
		position: relative !important;
		top: 1em !important;
		color: white !important;
		font-size: 2em !important;
	}

	#menu {
		position: relative !important;
		top: 2em !important;
		width: 96em !important;
		margin: auto !important;
		color: rgb(64,64,64) !important;
	}

	#menuMainList {
		height: 3em !important;
		width: calc(100% + 3em) !important;
		padding: 0 1em !important;
		border-radius: 4px !important;
		background: white !important;
	}

	#menuMainList > li {
		height: 3em !important;
		width: 12em !important;
		text-align: center !important;
	}

	#menuMainList > li > a {
		position: relative;
		top: 2px;
		border: none !important;
		padding: 0.75em 0 !important;
		margin: 0 !important;
		background: transparent !important;
		color: inherit !important;
	}

	#menuMainList > .current span {
		height: 2.6em !important;
		border-radius: 4px !important;
		border: none !important;
		background-color: rgb(59,162,229) !important;
	}

	#menuMainList > .current span > strong {
		position: relative !important;
		top: 2px !important;
	}

	#menuSub {
		background: none !important;
		border: none !important;
	}

	#menuSubList {
		position: relative !important;
		top: 2em !important;
	}

	#menuSubList a {
		border: none !important;
		margin-top: 2px !important;
		background: none !important;
		color: white !important;
	}

	#main {
		width: 62em !important;
		border: none !important;
		background: white !important;
	}

	.infopage #main {
		width: 100% !important;
	}

	#contents, #contentsInfo {
		width: 100% !important;
	}

	.col2l {
		width: 38em !important;
		margin-right: 1em !important;
	}

	.col2r {
		width: 38em !important;
		margin-left: 1em !important;
	}

	.submitFooter {
		background: none !important;
		color: inherit !important;
	}

	#help {
		width: 18em !important;
	}

	#help dd {
		border-bottom: 1px solid rgba(64,64,64,0.2) !important;
	}

	a {
		color: rgb(30,130,194) !important;
	}

	#statusInfo {
		position: absolute !important;
		top: 1em !important;
		right: 1em !important;
	}

	.note, .also {
		border: 1px solid rgba(64,64,64,0.2) !important;
	}
	
	input[src="images/paypal.png"] {
		display: block !important;
		height: 3em !important;
		margin: 1em auto !important;
	}
	
	input[src="images/paypal.png"]::after {
		content: 'Donate'
	}
}
