css:
@namespace url(http://www.w3.org/1999/xhtml);
 
@-moz-document domain("www.coosp.etr.u-szeged.hu") {
 
/* page */
body {
  background-color: rgb(200, 200, 200);
  font-size: 13px;
}
#header1 {
  width: 990px;
  margin: 0 auto;
}
#header{
  width: 990px;
  margin: 0 auto;
  height: 70px;
}
#header3 > ul > li > a {
  padding-top: 45px;
}
#header3 > ul > li {
  background-size: 30px;
  background-position: center 15px;
}
#header3 > ul > li.current {
  background-position: center -45px;
}
#header3 > ul > li:hover {
  background-color: #494949;
  background-position: center -45px;
}
#wrapper {
  padding-bottom: 0;
  background: rgba(240, 240, 240, 0.85);
}
#breadcrumb2 {
  background-color: rgba(250, 250, 250, 0.95);
  border: none;
  height: 5px;
  width: 990px;
  margin: 0 auto;
}
#breadcrumb > ul {
  display: none;
}
#mainsearch input {
  top: 25px;
}
#mainsearch input + ul {
  top: 57px;
}
#footer {
  margin-top: 0;
  width: 990px;
  margin: 0 auto;
}
.fixed {
  position: unset;
}
h1 {
  margin-right: 225px;
  margin-top: 5px;
}
h1 span.title {
  font-size: x-large;
  line-height: unset;
}
h1 a.imagebutton35 {
  margin-top: 0;
}
section.realcontent {
  padding-left: 10px;
  padding-right: 10px;
  width: 700px;
}
section.realcontent h1 {
  padding-left: 45px;
}
section.realcontent h2 {
  margin-top: 0;
  margin-bottom: 0;
}
section.scenes h2 {
  margin-top: 20px;
}
section.realcontent > div.icon {
  margin-left: 0;
}
 
/* sidebar */
aside.left {
  padding-left: 10px;
}
aside.left a.headerbutton.headerbutton_disabled_highlighted:hover,
aside.left a.headerbutton.headerbutton_highlighted:hover {
  background-position: 6px 5px;
}
aside.left li.current a.headerbutton:hover {
  background-position: 6px 5px;
}
aside.left a.headerbutton{
  background-position: 6px 5px;
  background-size: 16px;
}
aside.left a.headerbutton:hover {
  background-position: 6px -44px;
}
ul.bigvmenu {
  background: none;
}
ul.bigvmenu li a {
  padding: 5px;
}
 
/* scenelist */
#scenetreecontainer {
  background: none;
}
section .scenes {
  background: none;
}
section.scenes a.name {
  font-size: 15px;
}
section.scenes li > div {
  min-height: 45px;
  height: 45px;
  padding: 0;
}
.scene .scene_tools {
  width: unset;
  height: 35px;
  margin-top: 5px;
  clear: none;
}
.scene span.categorychooser {
  line-height: unset;
  margin: 0;
  max-width: 24px;
}
.scene span.categorychooser .c1text,
.scene span.categorychooser .c2text,
.scene span.categorychooser .c3text,
.scene span.categorychooser .c4text,
.scene span.categorychooser .c5text,
.scene span.categorychooser .c6text,
.scene span.categorychooser .c7text,
.scene span.categorychooser .c8text,
.scene span.categorychooser .c9text,
.scene span.categorychooser .c10text,
.scene span.categorychooser .c11text,
.scene span.categorychooser .c12text,
.scene span.categorychooser .c13text,
.scene span.categorychooser .c14text,
.scene span.categorychooser .c15text,
.scene span.categorychooser .c16text{
  display: none;
}
 
/* scene page */
.scene div.fav_code {
  margin-top: 25px;
}
.toolheader_description .header_description {
  background: none;
}
.toollist ul li.scene div.scene {
  padding: 0;
  min-height: 45px;
  height: 45px;
}
section.tools {
  background: none;
}
section.tools > div > ul {
  margin-top: 0;
  margin-bottom: 0;
}
section.members {
  background: none;
}
 
/* login */
#loginpage {
  background: rgba(240, 240, 240, 0.85);
  width: 990px;
  margin: 0 auto;
}
#loginbox section.msgs {
  padding: 5px;
  width: 680px;
  margin-left: 300px;
  margin-top: -400px;
}
#loginbox section.msgs #msg0, #loginbox section.msgs #msg1 {
  width: unset;
  margin: 0;
}
#loginbox section.msgs div.msg {
  background: none;
  outline: none;
}
section.ads {
  display: none;
}
 
/*loginform*/
#loginbox #loginleft {
  float:left;
  width: 300px;
  margin-left: 0;
  background: none;
  outline: none;
}
#loginbox #loginleft .logo {
  font-size: x-large;
}
#loginbox #loginleft input[type="submit"] {
  background-image: none;
  padding: 0;
  height: 30px;
  min-width: 90px;
}
#loginbox #loginleft input[type="text"], #loginbox #loginleft input[type="password"] {
  height: 25px;
}
 
}
