.watch6-button.yt-uix-button-dark  {
  color: #B4B4B4;
  text-shadow: 0 1px 1px #333;
  border-color: #111;  /* Only used in IE8 and under */
  background: #3F3F3F;
  background-image: -moz-linear-gradient(top, #4F4F4F, #2F2F2F);
  background-image: -webkit-gradient(linear, left top, left bottom, from(#4F4F4F), to(#2F2F2F));
  -moz-box-shadow: 0 1px 1px #111;
  -webkit-box-shadow: 0 1px 1px #111;
  box-shadow: 0 1px 1px #111;
  border-radius: 3px;
}

.watch6-button .yt-uix-button-icon,
.watch6-button.yt-uix-button-dark .yt-uix-button-icon {
    opacity: 1 !important;
}

.watch6-button.yt-uix-button-dark.start {
border-top-right-radius: 0px;
border-bottom-right-radius: 0px;
}
.watch6-button.yt-uix-button-dark.middle  {
border-top-right-radius: 0px;
border-bottom-right-radius: 0px;
border-top-left-radius: 0px;
border-bottom-left-radius: 0px;
}

.watch6-button.yt-uix-button-dark.end  {
border-top-left-radius: 0px;
border-bottom-left-radius: 0px;
}

.watch6-button.yt-uix-button-dark.yt-uix-button-text {
  border-color: transparent;
  background: none;
  -moz-box-shadow: none;
  -webkit-box-shadow: none;
  box-shadow: none;
}
.watch6-button.yt-uix-button-dark:hover,
.watch6-button.yt-uix-button-dark:focus {
  color: #FFF;
  text-shadow: 0 1px 1px #333;
  border-color: #111;  /* Only used in IE8 and under */
  background: #454545;
  background-image: -moz-linear-gradient(top, #555555, #353535);
  background-image: -webkit-gradient(linear, left top, left bottom, from(#555555), to(#353535));
  -moz-box-shadow: 0 1px 2px #111;
  -webkit-box-shadow: 0 1px 2px #111;
  box-shadow: 0 1px 2px #111;
}
.watch6-button.yt-uix-button-dark:active,
.watch6-button.yt-uix-button-dark.yt-uix-button-active {
  color: #CCC;
  text-shadow: 0 -1px 1px #000;
  border-color: #111;  /* Only used in IE8 and under */
  background: #333;
  border-bottom: 1px solid #444;
  filter: none;
  -moz-box-shadow: inset 0 2px 5px #111;
  -webkit-box-shadow: inset 0 2px 5px #111;
  box-shadow: inset 0 2px 5px #111;
}
.watch6-button.yt-uix-button-dark.yt-uix-button-toggled {
  color: #FFF;
  border-color: #111;  /* Only used in IE8 and under */
  border-bottom: 1px solid #333;
  background: #161616;
  filter: none;
  -moz-box-shadow: inset 0 2px 3px #111;
  -webkit-box-shadow: inset 0 2px 3px #111;
  box-shadow: inset 0 2px 3px #111;
}

.watch6-button.yt-uix-button-dark .yt-uix-button-arrow {
  border-top-color: #999;
}
.watch6-button.yt-uix-button-dark.yt-uix-button-reverse .yt-uix-button-arrow {
  border-top-color: transparent;
  border-bottom-color: #999;
}
.watch6-button.yt-uix-button-dark:hover .yt-uix-button-arrow,
.watch6-button.yt-uix-button-dark:focus .yt-uix-button-arrow {
  border-top-color: #AEAEAE;
}
.watch6-button.yt-uix-button-dark.yt-uix-button-reverse:hover .yt-uix-button-arrow,
.watch6-button.yt-uix-button-dark.yt-uix-button-reverse:focus .yt-uix-button-arrow {
  border-top-color: transparent;
  border-bottom-color: #AEAEAE;
}
.watch6-button.yt-uix-button-dark:active .yt-uix-button-arrow,
.watch6-button.yt-uix-button-dark.yt-uix-button-active .yt-uix-button-arrow {
  border-top-color: #AEAEAE;
}
.watch6-button.yt-uix-button-dark.yt-uix-button-reverse:active .yt-uix-button-arrow,
.watch6-button.yt-uix-button-dark.yt-uix-button-reverse.yt-uix-button-active .yt-uix-button-arrow {
  border-top-color: transparent;
  border-bottom-color: #AEAEAE;
}
.watch6-button.yt-uix-button-menu-dark {
  border-color: #999;
}
.watch6-button.yt-uix-button-group:hover .yt-uix-button-dark,
.watch6-button.yt-uix-button-group-active .yt-uix-button-dark {
  border-color: #111;  /* Only used in IE8 and under */
}
.watch6-button.yt-uix-button-dark[disabled],
.watch6-button.yt-uix-button-dark[disabled]:hover,
.watch6-button.yt-uix-button-dark[disabled]:focus,
.watch6-button.yt-uix-button-dark[disabled]:active,
.yt-uix-button-group .watch6-button.yt-uix-button-dark[disabled],
.yt-uix-button-group .watch6-button.yt-uix-button-dark[disabled]:hover,
.yt-uix-button-group .watch6-button.yt-uix-button-dark[disabled]:focus,
.yt-uix-button-group .watch6-button.yt-uix-button-dark[disabled]:active {
  color: #666;
  text-shadow: 0 1px 1px #333;
  border: none;
  background: #3C3C3C;
  background-image: -moz-linear-gradient(top, #3C3C3C, #3A3A3A);
  background-image: -webkit-gradient(linear, left top, left bottom, from(#3C3C3C), to(#3A3A3A));
  -moz-box-shadow: none;
  -webkit-box-shadow: none;
  box-shadow: none;
}
.watch6-button.yt-uix-button-dark[disabled]:hover .yt-uix-button-arrow,
.watch6-button.yt-uix-button-dark[disabled]:focus .yt-uix-button-arrow,
.watch6-button.yt-uix-button-dark[disabled]:active .yt-uix-button-arrow,
.watch6-button.yt-uix-button-dark.yt-uix-button-active[disabled] .yt-uix-button-arrow {
  border-top-color: #666;
}
.watch6-button.yt-uix-button-dark.yt-uix-button-reverse[disabled]:hover .yt-uix-button-arrow,
.watch6-button.yt-uix-button-dark.yt-uix-button-reverse[disabled]:focus .yt-uix-button-arrow,
.watch6-button.yt-uix-button-dark.yt-uix-button-reverse[disabled]:active .yt-uix-button-arrow,
.watch6-button.yt-uix-button-dark.yt-uix-button-active.yt-uix-button-reverse[disabled] .yt-uix-button-arrow {
  border-bottom-color: #666;
}






































#watch-tray,
#watch-bar {
  width: 970px;
  margin: 0 auto;

  line-height: 1;
  /*-font-size: 1em;*/
}

.watch6 #watch-bar {
  position: relative;
  height: 45px;
  line-height: 45px;
  overflow: hidden;
}

#watch-title {
  height: 2.25em; /* matches h1 line-height */
  overflow: hidden;
  color: #E4E4E4 !important;
  /*-text-shadow: 0 1px 1px #333 !important;

  -font-size: 16px;
  -height: 25px !important;*/
  font-size: 18px;
  font-size: auto;
  line-height: 2.0em;
  line-height: 36px;
  height: 36px;
}
#watch-title .long-title {
  font-size: 0.9em;
  /*-font-size: 22px;
  -font-size: 36px;*/
  letter-spacing: -0.5px;
}
#watch-title a {
  color: #E4E4E4;
}
#watch-title img {
  margin: 0 .5em;
  vertical-align: middle;
  display: inline-block;

}
#watch-bar-channel {
  position: absolute;
  top: 0;
  left: 0;
}
#watch-bar-actions {
  position: absolute;
  top: 0;
  right: 0;
  color: #999;
}
#watch-bar-switcher {
  text-align: center;
  display: block;
}
.watch6 #watch-bar-channel .yt-uix-button,
.watch6 #watch-bar-switcher .yt-uix-button,
.watch6 #watch-bar-actions .yt-uix-button {
  position: relative;
  top: -2px;
  height: 35px;
  margin: 0;
  padding: 0;
  font-size: 14px;
}
#watch-bar-channel .yt-uix-button-content,
#watch-bar-switcher .yt-uix-button-content,
#channel-bar-switcher .yt-uix-button-content {
  padding: 0 40px;
  line-height: 35px;
}
#watch-bar-channel .yt-uix-button-content {
  padding-right: 10px;
  color: #E4E4E4;
}
#watch-bar-channel .ux6-thumb {
  position: absolute;
  left: 2px; /* accomodate the padding in webkit. */
  top: 2px;
  top: 7px;
  width: 31px;
  height: 31px;
  padding: 0;
  vertical-align: middle;
  border: none;
  -webkit-border-radius: 3px;
  -moz-border-radius: 3px;
  border-radius: 3px;
  overflow:hidden;
}
#watch-bar-channel .ux6-thumb img {
height:31px;
vertical-align: baseline;
}
#watch-bar-channel .ux-thumb img {
  height: 31px;
  vertical-align: baseline;
}

#watch-bar-channel .yt-subscription-button .yt-uix-button-arrow {
  display: none;
}
#watch-bar-channel .yt-subscription-button .yt-uix-button-content {
  display: inline-block;
  padding: 0 5px 0 0;
  margin: 0 0 0 5px;
  width: 26px;
  height: 25px;
  text-indent: -10000px;
  position: relative;
  top: 1px;
  background: url(https://s.ytimg.com/yt/img/watch6-subscribe.png) 0 0 no-repeat;
  background-image: url(data:image/png;base64,);
}
#watch-bar-channel .yt-subscription-button:hover .yt-uix-button-content {
  background-image: url(https://s.ytimg.com/yt/img/watch6-subscribe-hover.png);
  background-image: url(data:image/png;base64,);
}
#watch-bar-channel .yt-subscription-button.subscribed .yt-uix-button-content {
  background-image: url(https://s.ytimg.com/yt/img/watch6-subscribed.png);
  background-image: url(data:image/png;base64,);
}
#watch-bar-channel .yt-subscription-button.subscribed:hover .yt-uix-button-content {
  background-image: url(https://s.ytimg.com/yt/img/watch6-subscribed-hover.png);
  background-image: url(data:image/png;base64,);
}

#watch-bar-channel .yt-subscription-button.subscribed {
  /* TODO(teh): Fix subscribed class to not change the background globally. */
  background: #3F3F3F;
  background-image: -moz-linear-gradient(top, #4F4F4F, #2F2F2F);
  background-image: -webkit-gradient(linear, left top, left bottom, from(#4F4F4F), to(#2F2F2F));
}















