css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain(facebook.com) {
* {
  background-color: #171717 !important;
  color: #ffffff !important;
}
html, body {
  background: none !important;
  background: #171717 !important;
  color: #ffffff !important;
}
pre {
  background-color: #171717 !important;
  border: 1px solid darkgray !important
}


a:link { 
  color: #86B0E1 !important
}
a:visited {
  color: gray !important
}
label {
  color: gainsboro!important
}
input {
  color: gainsboro!important;
  background-color: #000 !important
}
select {
  color: gainsboro!important;
  background-color: #000 !important
}
textarea {
  color: gainsboro !important;
  background-color: #171717 !important
}


.clearfix {
  background: none !important;
  background-color: #171717 !important
}
.body {
  background: none !important;
  color: #ffffff !important
}
#navigator {
  background: #171717 !important
}
#app_list .list_item {
  background: none !important
}
#buddy_count {
  color: #FFF !important
}
.buddy_count_num strong {
  color: #FFF !important;
}
.textarea {
  color: #FFF !important;
}
.link_btn_style {
  background-color: #333 !important
}
.wrapper {
  color: #ffffff !important
}
.wrapper em {
  color: #FFF !important
}

#sidebar a.go_home {
background: #171717 url(http://i16.photobucket.com/albums/b43/rustyshackleford19/facebook_logo.jpg) repeat-x !important;
}

#navigator {
background: #171717 url(http://i16.photobucket.com/albums/b43/rustyshackleford19/backgroundchocollama.png) repeat-x !important;
}

#gift_message_body_wrapper {
  background-image: url(data:image/png;base64,%3D%3D) !important
}

#gift_message_body {
  width: 0 !important;
  height: 6px !important;
  padding-left: 6px !important;
  background-image: url(data:image/gif;base64,%3D%3D) !important }

.gift_topright {
  width: 0 !important;
  height: 6px !important;
  padding-left: 6px !important;
  background-image: url(data:image/gif;base64,%3D) !important }

.gift_topleft {
  width: 0 !important;
  height: 6px !important;
  padding-left: 6px !important;
  background-image: url(data:image/gif;base64,%3D ) !important }

.gift_bottomright {
  width: 0 !important;
  height: 6px !important;
  padding-left: 6px !important;
  background-image: url(data:image/gif;base64,) !important }

.gift_bottomleft {
  width: 0 !important;
  height: 6px !important;
  padding-left: 6px !important;
  background-image: url(data:image/gif;base64,) !important }


img[src*="http://static.ak.fbcdn.net/images/im_online.gif"] {
  width: 0 !important;
  height: 10px !important;
  padding-left: 15px !important;
  background-image: url(data:image/gif;base64,%3D%3D) !important
}

}
